import React from 'react';

const Logo = ({ size = 'default', className = '' }) => {
  const sizeClasses = {
    small: 'h-8 w-8',
    default: 'h-10 w-10',
    large: 'h-12 w-12'
  };

  const textSizes = {
    small: 'text-lg',
    default: 'text-xl',
    large: 'text-2xl'
  };

  return (
    <div className={`flex items-center ${className}`}>
      {/* Logo Icon */}
      <div className={`${sizeClasses[size]} flex-shrink-0 rounded-lg bg-gradient-to-br from-primary-500 to-primary-600 flex items-center justify-center shadow-lg`}>
        <svg 
          className="w-5/6 h-5/6 text-white" 
          fill="currentColor" 
          viewBox="0 0 24 24"
        >
          {/* Traffic Light Icon */}
          <path d="M12 2C13.1 2 14 2.9 14 4V6C14 7.1 13.1 8 12 8C10.9 8 10 7.1 10 6V4C10 2.9 10.9 2 12 2Z"/>
          <path d="M12 10C13.1 10 14 10.9 14 12V14C14 15.1 13.1 16 12 16C10.9 16 10 15.1 10 14V12C10 10.9 10.9 10 12 10Z"/>
          <path d="M12 18C13.1 18 14 18.9 14 20V22C14 23.1 13.1 24 12 24C10.9 24 10 23.1 10 22V20C10 18.9 10.9 18 12 18Z"/>
          <path d="M8 4C8 2.9 7.1 2 6 2C4.9 2 4 2.9 4 4V6C4 7.1 4.9 8 6 8C7.1 8 8 7.1 8 6V4Z"/>
          <path d="M8 12C8 10.9 7.1 10 6 10C4.9 10 4 10.9 4 12V14C4 15.1 4.9 16 6 16C7.1 16 8 15.1 8 14V12Z"/>
          <path d="M8 20C8 18.9 7.1 18 6 18C4.9 18 4 18.9 4 20V22C4 23.1 4.9 24 6 24C7.1 24 8 23.1 8 22V20Z"/>
          <path d="M20 4C20 2.9 19.1 2 18 2C16.9 2 16 2.9 16 4V6C16 7.1 16.9 8 18 8C19.1 8 20 7.1 20 6V4Z"/>
          <path d="M20 12C20 10.9 19.1 10 18 10C16.9 10 16 10.9 16 12V14C16 15.1 16.9 16 18 16C19.1 16 20 15.1 20 14V12Z"/>
          <path d="M20 20C20 18.9 19.1 18 18 18C16.9 18 16 18.9 16 20V22C16 23.1 16.9 24 18 24C19.1 24 20 23.1 20 22V20Z"/>
        </svg>
      </div>
      
      {/* Logo Text */}
      <div className="ml-3">
        <h1 className={`font-bold text-gray-900 ${textSizes[size]}`}>
          <span className="text-primary-600">e</span>-Traffic
        </h1>
        <p className="text-xs text-gray-500 font-medium">System</p>
      </div>
    </div>
  );
};

export default Logo;
