import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { violationsAPI, adminAPI } from '../../services/api';
import { Search, Filter, Download, Eye, Edit, Trash2, Plus } from 'lucide-react';
import LoadingSpinner from '../../components/LoadingSpinner';
import toast from 'react-hot-toast';

const Violations = () => {
  const [filters, setFilters] = useState({
    page: 1,
    limit: 10,
    search: '',
    status: '',
    enforcer_id: '',
    start_date: '',
    end_date: '',
    violation_type: '',
  });
  const [showAddModal, setShowAddModal] = useState(false);
  const [formErrors, setFormErrors] = useState({});
  const queryClient = useQueryClient();

  const { data: violationsResponse, isLoading, error } = useQuery({
    queryKey: ['violations', filters],
    queryFn: () => violationsAPI.getViolations(filters),
    placeholderData: (previousData) => previousData,
  });

  // Fetch enforcers for the form
  const { data: enforcersResponse } = useQuery({
    queryKey: ['enforcers'],
    queryFn: () => adminAPI.getEnforcers(),
    enabled: showAddModal, // Only fetch when modal is open
  });

  // Extract data from response
  const data = violationsResponse?.data?.data;
  const enforcers = enforcersResponse?.data?.data?.enforcers || [];

  // Debug logging
  console.log('Violations API Response:', violationsResponse);
  console.log('Extracted data:', data);
  console.log('Violations array:', data?.violations);
  console.log('Pagination:', data?.pagination);

  // Create violation mutation
  const createViolationMutation = useMutation({
    mutationFn: (violationData) => violationsAPI.createAdminViolation(violationData),
    onSuccess: () => {
      // Invalidate the specific violations query with current filters
      queryClient.invalidateQueries(['violations', filters]);
      queryClient.invalidateQueries(['adminDashboard']);
      setShowAddModal(false);
      setFormErrors({});
      toast.success('Violation created successfully');
    },
    onError: (error) => {
      if (error.response?.data?.details) {
        const errors = {};
        error.response.data.details.forEach(detail => {
          errors[detail.param || detail.path] = detail.msg;
        });
        setFormErrors(errors);
      } else {
        setFormErrors({});
        toast.error(error.response?.data?.message || 'Failed to create violation');
      }
    }
  });

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: 1, // Reset to first page when filters change
    }));
  };

  const handlePageChange = (page) => {
    setFilters(prev => ({ ...prev, page }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const violationData = {
      enforcer_id: parseInt(formData.get('enforcer_id')),
      violator_name: formData.get('violator_name'),
      violator_license: formData.get('violator_license'),
      violator_phone: formData.get('violator_phone'),
      violator_address: formData.get('violator_address'),
      vehicle_plate: formData.get('vehicle_plate'),
      vehicle_model: formData.get('vehicle_model'),
      vehicle_color: formData.get('vehicle_color'),
      violation_type: formData.get('violation_type'),
      violation_description: formData.get('violation_description'),
      location: formData.get('location'),
      latitude: parseFloat(formData.get('latitude')) || null,
      longitude: parseFloat(formData.get('longitude')) || null,
      fine_amount: parseFloat(formData.get('fine_amount')),
      status: formData.get('status'),
      notes: formData.get('notes'),
      due_date: formData.get('due_date')
    };

    createViolationMutation.mutate(violationData);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <p className="text-danger-600">Failed to load violations</p>
      </div>
    );
  }

  // Extract violations and pagination from the correct response structure
  const { violations, pagination } = data || {};

  return (
    <div className="space-y-6">
      {/* Page header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Violations</h1>
          <p className="mt-1 text-sm text-gray-500">
            Manage and monitor traffic violations
          </p>
        </div>
        <div className="flex gap-2">
          <button 
            onClick={() => setShowAddModal(true)}
            className="btn-primary"
          >
            <Plus className="h-4 w-4 mr-2" />
            Add Violation
          </button>
          <button 
            onClick={() => queryClient.invalidateQueries(['violations'])}
            className="btn-secondary"
          >
            <Search className="h-4 w-4 mr-2" />
            Refresh
          </button>
          <button 
            onClick={() => {
              console.log('Testing violations API...');
              violationsAPI.getViolations(filters).then(response => {
                console.log('Manual API test response:', response);
              }).catch(error => {
                console.error('Manual API test error:', error);
              });
            }}
            className="btn-secondary"
          >
            Test API
          </button>
          <button className="btn-secondary">
            <Download className="h-4 w-4 mr-2" />
            Export
          </button>
        </div>
      </div>

      {/* Debug Info */}
      {process.env.NODE_ENV === 'development' && (
        <div className="bg-gray-100 p-4 rounded-lg text-sm">
          <h3 className="font-semibold mb-2">Debug Info:</h3>
          <p>Has Response: {violationsResponse ? 'Yes' : 'No'}</p>
          <p>Response Data: {JSON.stringify(violationsResponse?.data, null, 2)}</p>
          <p>Extracted Data: {JSON.stringify(data, null, 2)}</p>
          <p>Violations Count: {violations?.length || 0}</p>
          <p>Pagination: {JSON.stringify(pagination, null, 2)}</p>
        </div>
      )}

      {/* Filters */}
      <div className="card">
        <div className="card-body">
          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
            {/* Search */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Search
              </label>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <input
                  type="text"
                  className="input pl-10"
                  placeholder="Search violations..."
                  value={filters.search}
                  onChange={(e) => handleFilterChange('search', e.target.value)}
                />
              </div>
            </div>

            {/* Status filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Status
              </label>
              <select
                className="input"
                value={filters.status}
                onChange={(e) => handleFilterChange('status', e.target.value)}
              >
                <option value="">All Status</option>
                <option value="pending">Pending</option>
                <option value="issued">Issued</option>
                <option value="paid">Paid</option>
                <option value="disputed">Disputed</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>

            {/* Violation type filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Violation Type
              </label>
              <select
                className="input"
                value={filters.violation_type}
                onChange={(e) => handleFilterChange('violation_type', e.target.value)}
              >
                <option value="">All Types</option>
                <option value="Speeding">Speeding</option>
                <option value="Red Light">Red Light</option>
                <option value="Parking">Parking</option>
                <option value="Reckless Driving">Reckless Driving</option>
                <option value="DUI">DUI</option>
              </select>
            </div>

            {/* Date range */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Start Date
              </label>
              <input
                type="date"
                className="input"
                value={filters.start_date}
                onChange={(e) => handleFilterChange('start_date', e.target.value)}
              />
            </div>
          </div>

          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3 mt-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                End Date
              </label>
              <input
                type="date"
                className="input"
                value={filters.end_date}
                onChange={(e) => handleFilterChange('end_date', e.target.value)}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Items per page
              </label>
              <select
                className="input"
                value={filters.limit}
                onChange={(e) => handleFilterChange('limit', parseInt(e.target.value))}
              >
                <option value={10}>10</option>
                <option value={25}>25</option>
                <option value={50}>50</option>
                <option value={100}>100</option>
              </select>
            </div>

            <div className="flex items-end">
              <button
                onClick={() => setFilters({
                  page: 1,
                  limit: 10,
                  search: '',
                  status: '',
                  enforcer_id: '',
                  start_date: '',
                  end_date: '',
                  violation_type: '',
                })}
                className="btn-secondary"
              >
                <Filter className="h-4 w-4 mr-2" />
                Clear Filters
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Violations table */}
      <div className="card">
        <div className="card-body">
          <div className="overflow-x-auto">
            <table className="table">
              <thead className="table-header">
                <tr>
                  <th className="table-header-cell">Violation #</th>
                  <th className="table-header-cell">Violator</th>
                  <th className="table-header-cell">Vehicle</th>
                  <th className="table-header-cell">Type</th>
                  <th className="table-header-cell">Fine</th>
                  <th className="table-header-cell">Status</th>
                  <th className="table-header-cell">Enforcer</th>
                  <th className="table-header-cell">Date</th>
                  <th className="table-header-cell">Actions</th>
                </tr>
              </thead>
              <tbody className="table-body">
                {violations?.map((violation) => (
                  <tr key={violation.id} className="table-row">
                    <td className="table-cell font-medium">
                      {violation.violation_number}
                    </td>
                    <td className="table-cell">
                      <div>
                        <div className="font-medium">{violation.violator_name}</div>
                        <div className="text-sm text-gray-500">
                          {violation.violator_phone}
                        </div>
                      </div>
                    </td>
                    <td className="table-cell">
                      <div>
                        <div className="font-medium">{violation.vehicle_plate}</div>
                        <div className="text-sm text-gray-500">
                          {violation.vehicle_model} {violation.vehicle_color}
                        </div>
                      </div>
                    </td>
                    <td className="table-cell">{violation.violation_type}</td>
                    <td className="table-cell font-medium">
                      ${violation.fine_amount}
                    </td>
                    <td className="table-cell">
                      <StatusBadge status={violation.status} />
                    </td>
                    <td className="table-cell">
                      <div>
                        <div className="font-medium">{violation.enforcer_name}</div>
                        <div className="text-sm text-gray-500">
                          {violation.enforcer_badge}
                        </div>
                      </div>
                    </td>
                    <td className="table-cell">
                      {new Date(violation.created_at).toLocaleDateString()}
                    </td>
                    <td className="table-cell">
                      <div className="flex space-x-2">
                        <button className="text-primary-600 hover:text-primary-900">
                          <Eye className="h-4 w-4" />
                        </button>
                        <button className="text-warning-600 hover:text-warning-900">
                          <Edit className="h-4 w-4" />
                        </button>
                        <button className="text-danger-600 hover:text-danger-900">
                          <Trash2 className="h-4 w-4" />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {(!violations || violations.length === 0) && (
              <div className="text-center py-8 text-gray-500">
                No violations found
              </div>
            )}
          </div>

          {/* Pagination */}
          {pagination && pagination.total > 1 && (
            <div className="mt-6 flex items-center justify-between">
              <div className="text-sm text-gray-700">
                Showing {((pagination.current - 1) * filters.limit) + 1} to{' '}
                {Math.min(pagination.current * filters.limit, pagination.totalRecords)} of{' '}
                {pagination.totalRecords} results
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => handlePageChange(pagination.current - 1)}
                  disabled={pagination.current === 1}
                  className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Previous
                </button>
                <span className="px-3 py-2 text-sm text-gray-700">
                  Page {pagination.current} of {pagination.total}
                </span>
                <button
                  onClick={() => handlePageChange(pagination.current + 1)}
                  disabled={pagination.current === pagination.total}
                  className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Next
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Add Violation Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            <h2 className="text-xl font-bold mb-4">Add New Violation</h2>
            
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {/* Enforcer Selection */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Enforcer *
                  </label>
                  <select
                    name="enforcer_id"
                    required
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.enforcer_id ? 'border-red-300' : 'border-gray-300'
                    }`}
                  >
                    <option value="">Select Enforcer</option>
                    {enforcers.map(enforcer => (
                      <option key={enforcer.id} value={enforcer.id}>
                        {enforcer.full_name} ({enforcer.badge_number})
                      </option>
                    ))}
                  </select>
                  {formErrors.enforcer_id && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.enforcer_id}</p>
                  )}
                </div>

                {/* Violation Type */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Violation Type *
                  </label>
                  <select
                    name="violation_type"
                    required
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.violation_type ? 'border-red-300' : 'border-gray-300'
                    }`}
                  >
                    <option value="">Select Type</option>
                    <option value="Speeding">Speeding</option>
                    <option value="Red Light">Red Light</option>
                    <option value="Parking">Parking</option>
                    <option value="Reckless Driving">Reckless Driving</option>
                    <option value="DUI">DUI</option>
                    <option value="Illegal U-turn">Illegal U-turn</option>
                    <option value="No Seatbelt">No Seatbelt</option>
                    <option value="Texting While Driving">Texting While Driving</option>
                  </select>
                  {formErrors.violation_type && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.violation_type}</p>
                  )}
                </div>

                {/* Violator Name */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Violator Name *
                  </label>
                  <input
                    type="text"
                    name="violator_name"
                    required
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.violator_name ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.violator_name && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.violator_name}</p>
                  )}
                </div>

                {/* License Number */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    License Number
                  </label>
                  <input
                    type="text"
                    name="violator_license"
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.violator_license ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.violator_license && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.violator_license}</p>
                  )}
                </div>

                {/* Phone Number */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Phone Number
                  </label>
                  <input
                    type="tel"
                    name="violator_phone"
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.violator_phone ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.violator_phone && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.violator_phone}</p>
                  )}
                </div>

                {/* Address */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Address
                  </label>
                  <input
                    type="text"
                    name="violator_address"
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.violator_address ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.violator_address && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.violator_address}</p>
                  )}
                </div>

                {/* Vehicle Plate */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Vehicle Plate *
                  </label>
                  <input
                    type="text"
                    name="vehicle_plate"
                    required
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.vehicle_plate ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.vehicle_plate && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.vehicle_plate}</p>
                  )}
                </div>

                {/* Vehicle Model */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Vehicle Model
                  </label>
                  <input
                    type="text"
                    name="vehicle_model"
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.vehicle_model ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.vehicle_model && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.vehicle_model}</p>
                  )}
                </div>

                {/* Vehicle Color */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Vehicle Color
                  </label>
                  <input
                    type="text"
                    name="vehicle_color"
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.vehicle_color ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.vehicle_color && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.vehicle_color}</p>
                  )}
                </div>

                {/* Fine Amount */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Fine Amount ($) *
                  </label>
                  <input
                    type="number"
                    name="fine_amount"
                    required
                    min="0"
                    step="0.01"
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.fine_amount ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.fine_amount && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.fine_amount}</p>
                  )}
                </div>

                {/* Status */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Status *
                  </label>
                  <select
                    name="status"
                    required
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.status ? 'border-red-300' : 'border-gray-300'
                    }`}
                  >
                    <option value="pending">Pending</option>
                    <option value="issued">Issued</option>
                    <option value="paid">Paid</option>
                    <option value="disputed">Disputed</option>
                    <option value="cancelled">Cancelled</option>
                  </select>
                  {formErrors.status && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.status}</p>
                  )}
                </div>

                {/* Due Date */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Due Date
                  </label>
                  <input
                    type="date"
                    name="due_date"
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.due_date ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.due_date && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.due_date}</p>
                  )}
                </div>

                {/* Location */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Location *
                  </label>
                  <input
                    type="text"
                    name="location"
                    required
                    placeholder="Street address or landmark"
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.location ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.location && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.location}</p>
                  )}
                </div>

                {/* Latitude */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Latitude
                  </label>
                  <input
                    type="number"
                    name="latitude"
                    step="any"
                    placeholder="Optional GPS coordinates"
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.latitude ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.latitude && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.latitude}</p>
                  )}
                </div>

                {/* Longitude */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Longitude
                  </label>
                  <input
                    type="number"
                    name="longitude"
                    step="any"
                    placeholder="Optional GPS coordinates"
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.longitude ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.longitude && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.longitude}</p>
                  )}
                </div>
              </div>

              {/* Violation Description */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Violation Description
                </label>
                <textarea
                  name="violation_description"
                  rows="3"
                  placeholder="Detailed description of the violation..."
                  className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                    formErrors.violation_description ? 'border-red-300' : 'border-gray-300'
                  }`}
                />
                {formErrors.violation_description && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.violation_description}</p>
                )}
              </div>

              {/* Notes */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Additional Notes
                </label>
                <textarea
                  name="notes"
                  rows="2"
                  placeholder="Any additional information..."
                  className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                    formErrors.notes ? 'border-red-300' : 'border-gray-300'
                  }`}
                />
                {formErrors.notes && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.notes}</p>
                )}
              </div>

              <div className="flex gap-3 pt-4">
                <button
                  type="submit"
                  disabled={createViolationMutation.isPending}
                  className="flex-1 btn-primary"
                >
                  {createViolationMutation.isPending ? 'Creating...' : 'Create Violation'}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowAddModal(false);
                    setFormErrors({});
                  }}
                  className="flex-1 btn-secondary"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

const StatusBadge = ({ status }) => {
  const statusConfig = {
    pending: { color: 'warning', text: 'Pending' },
    issued: { color: 'info', text: 'Issued' },
    paid: { color: 'success', text: 'Paid' },
    disputed: { color: 'danger', text: 'Disputed' },
    cancelled: { color: 'secondary', text: 'Cancelled' },
  };

  const config = statusConfig[status] || statusConfig.pending;

  return (
    <span className={`badge-${config.color}`}>
      {config.text}
    </span>
  );
};

export default Violations;
