import React from 'react';
import { useQuery } from '@tanstack/react-query';
import { adminAPI } from '../../services/api';
import {
  Users,
  FileText,
  DollarSign,
  AlertTriangle,
  CheckCircle,
  Clock,
  XCircle,
} from 'lucide-react';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
} from 'recharts';
import LoadingSpinner from '../../components/LoadingSpinner';

const COLORS = ['#3b82f6', '#ef4444', '#f59e0b', '#22c55e', '#8b5cf6'];

const StatCard = ({ title, value, icon: Icon, change, color = 'primary' }) => (
  <div className="card">
    <div className="card-body">
      <div className="flex items-center">
        <div className={`flex-shrink-0 p-3 rounded-md bg-${color}-100`}>
          <Icon className={`h-6 w-6 text-${color}-600`} />
        </div>
        <div className="ml-5 w-0 flex-1">
          <dl>
            <dt className="text-sm font-medium text-gray-500 truncate">{title}</dt>
            <dd className="text-lg font-medium text-gray-900">{value}</dd>
          </dl>
        </div>
      </div>
      {change && (
        <div className="mt-4">
          <div className="text-sm">
            <span className={`font-medium ${change > 0 ? 'text-success-600' : 'text-danger-600'}`}>
              {change > 0 ? '+' : ''}{change}%
            </span>
            <span className="text-gray-500"> from last month</span>
          </div>
        </div>
      )}
    </div>
  </div>
);

const StatusBadge = ({ status }) => {
  const statusConfig = {
    pending: { color: 'warning', icon: Clock, text: 'Pending' },
    issued: { color: 'info', icon: AlertTriangle, text: 'Issued' },
    paid: { color: 'success', icon: CheckCircle, text: 'Paid' },
    disputed: { color: 'danger', icon: XCircle, text: 'Disputed' },
    cancelled: { color: 'secondary', icon: XCircle, text: 'Cancelled' },
  };

  const config = statusConfig[status] || statusConfig.pending;
  const Icon = config.icon;

  return (
    <span className={`badge-${config.color} flex items-center`}>
      <Icon className="h-3 w-3 mr-1" />
      {config.text}
    </span>
  );
};

const AdminDashboard = () => {
  const { data: dashboardResponse, isLoading, error } = useQuery({
    queryKey: ['adminDashboard'],
    queryFn: adminAPI.getDashboard,
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  // Extract data from response (server wraps payload in data)
  const data = dashboardResponse?.data?.data;

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <p className="text-danger-600">Failed to load dashboard data</p>
      </div>
    );
  }

  // data already extracted above

  // Prepare chart data
  const monthlyData = data?.violationsByMonth?.map(item => ({
    month: item.month,
    violations: item.count,
  })) || [];

  const statusData = data?.violationsByStatus?.map(item => ({
    name: item.status,
    value: item.count,
  })) || [];

  return (
    <div className="space-y-6">
      {/* Page header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
        <p className="mt-1 text-sm text-gray-500">
          Overview of traffic violations and system statistics
        </p>
      </div>

      {/* Stats cards */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        <StatCard
          title="Total Violations"
          value={data?.totalViolations || 0}
          icon={FileText}
          color="primary"
        />
        <StatCard
          title="Active Enforcers"
          value={data?.activeEnforcers || 0}
          icon={Users}
          color="success"
        />
        <StatCard
          title="Total Enforcers"
          value={data?.totalEnforcers || 0}
          icon={Users}
          color="info"
        />
        <StatCard
          title="Total Fines Collected"
          value={`$${(data?.totalFines || 0).toLocaleString()}`}
          icon={DollarSign}
          color="warning"
        />
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 gap-6 lg:grid-cols-2">
        {/* Monthly violations chart */}
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-medium text-gray-900">Violations by Month</h3>
          </div>
          <div className="card-body">
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={monthlyData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="violations" fill="#3b82f6" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>

        {/* Status distribution chart */}
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-medium text-gray-900">Violations by Status</h3>
          </div>
          <div className="card-body">
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={statusData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {statusData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      </div>

      {/* Recent violations */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-medium text-gray-900">Recent Violations</h3>
        </div>
        <div className="card-body">
          <div className="overflow-x-auto">
            <table className="table">
              <thead className="table-header">
                <tr>
                  <th className="table-header-cell">Violation #</th>
                  <th className="table-header-cell">Violator</th>
                  <th className="table-header-cell">Type</th>
                  <th className="table-header-cell">Fine</th>
                  <th className="table-header-cell">Status</th>
                  <th className="table-header-cell">Enforcer</th>
                  <th className="table-header-cell">Date</th>
                </tr>
              </thead>
              <tbody className="table-body">
                {data?.recentViolations?.map((violation) => (
                  <tr key={violation.id} className="table-row">
                    <td className="table-cell font-medium">{violation.violation_number}</td>
                    <td className="table-cell">{violation.violator_name}</td>
                    <td className="table-cell">{violation.violation_type}</td>
                    <td className="table-cell">${violation.fine_amount}</td>
                    <td className="table-cell">
                      <StatusBadge status={violation.status} />
                    </td>
                    <td className="table-cell">{violation.enforcer_name}</td>
                    <td className="table-cell">
                      {new Date(violation.created_at).toLocaleDateString()}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            {(!data?.recentViolations || data.recentViolations.length === 0) && (
              <div className="text-center py-8 text-gray-500">
                No recent violations found
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
