const EnforcersReport = ({ filters, setFilters }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [reportData, setReportData] = useState(null);
  const [error, setError] = useState(null);

  const generateReport = async () => {
    if (!filters.startDate || !filters.endDate) {
      setError('Please select both start and end dates');
      return;
    }
    
    setIsLoading(true);
    setError(null);
    try {
      const response = await reportsAPI.getEnforcersReport({
        start_date: filters.startDate,
        end_date: filters.endDate
      });
      
      if (response.data.success) {
        setReportData(response.data.data);
      } else {
        setError('Failed to generate report');
      }
    } catch (error) {
      console.error('Failed to generate report:', error);
      setError(error.response?.data?.error || 'Failed to generate report');
    } finally {
      setIsLoading(false);
    }
  };

  const exportReport = (format) => {
    if (!reportData) return;
    
    if (format === 'json') {
      const dataStr = JSON.stringify(reportData, null, 2);
      const dataBlob = new Blob([dataStr], { type: 'application/json' });
      const url = URL.createObjectURL(dataBlob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `enforcers-report-${filters.startDate}-to-${filters.endDate}.json`;
      link.click();
      URL.revokeObjectURL(url);
    } else if (format === 'csv') {
      const params = new URLSearchParams({
        start_date: filters.startDate,
        end_date: filters.endDate,
        format: 'csv'
      });
      
      const url = `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/reports/enforcers?${params}`;
      const link = document.createElement('a');
      link.href = url;
      link.download = `enforcers-report-${filters.startDate}-to-${filters.endDate}.csv`;
      link.click();
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-xl font-bold text-gray-900">Enforcers Performance Report</h2>
          <p className="text-gray-600">Track enforcer productivity and performance metrics</p>
        </div>
        <ExportButtons onExport={exportReport} reportType="enforcers" isLoading={isLoading} hasData={!!reportData} />
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <div className="flex">
            <AlertCircle className="h-5 w-5 text-red-400" />
            <div className="ml-3">
              <p className="text-sm text-red-800">{error}</p>
            </div>
          </div>
        </div>
      )}

      <div className="card">
        <div className="card-body">
          <div className="flex gap-4 items-end mb-4">
            <DateRangePicker
              startDate={filters.startDate}
              endDate={filters.endDate}
              onStartDateChange={(date) => setFilters(prev => ({ ...prev, startDate: date }))}
              onEndDateChange={(date) => setFilters(prev => ({ ...prev, endDate: date }))}
            />
            <button
              onClick={generateReport}
              disabled={isLoading}
              className="btn-primary"
            >
              {isLoading ? <LoadingSpinner size="sm" /> : 'Generate Report'}
            </button>
          </div>

          {reportData && (
            <div className="space-y-6">
              {/* Summary Stats */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-blue-600">Total Enforcers</h4>
                  <p className="text-2xl font-bold text-blue-900">{reportData.summary.total_enforcers}</p>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-green-600">Total Violations</h4>
                  <p className="text-2xl font-bold text-green-900">{reportData.summary.total_violations}</p>
                </div>
                <div className="bg-yellow-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-yellow-600">Total Fines</h4>
                  <p className="text-2xl font-bold text-yellow-900">${reportData.summary.total_fines.toFixed(2)}</p>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-purple-600">Avg Collection Rate</h4>
                  <p className="text-2xl font-bold text-purple-900">{reportData.summary.avg_collection_rate}%</p>
                </div>
              </div>

              {/* Performance Chart */}
              <div className="card">
                <div className="card-header">
                  <h4 className="text-lg font-medium">Enforcer Performance Comparison</h4>
                </div>
                <div className="card-body">
                  <div className="h-64">
                    <ResponsiveContainer width="100%" height="100%">
                      <BarChart data={reportData.enforcers}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="full_name" />
                        <YAxis />
                        <Tooltip />
                        <Bar dataKey="total_violations" fill="#3b82f6" name="Violations Issued" />
                        <Bar dataKey="collected_fines" fill="#10b981" name="Fines Collected" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>
                </div>
              </div>

              {/* Performance Table */}
              <div className="card">
                <div className="card-header">
                  <h4 className="text-lg font-medium">Detailed Performance</h4>
                </div>
                <div className="card-body">
                  <div className="overflow-x-auto">
                    <table className="table">
                      <thead className="table-header">
                        <tr>
                          <th className="table-header-cell">Enforcer</th>
                          <th className="table-header-cell">Badge #</th>
                          <th className="table-header-cell">Violations</th>
                          <th className="table-header-cell">Total Fines</th>
                          <th className="table-header-cell">Collected</th>
                          <th className="table-header-cell">Collection Rate</th>
                          <th className="table-header-cell">Today</th>
                          <th className="table-header-cell">This Month</th>
                        </tr>
                      </thead>
                      <tbody className="table-body">
                        {reportData.enforcers.map((enforcer, index) => (
                          <tr key={index} className="table-row">
                            <td className="table-cell font-medium">{enforcer.full_name}</td>
                            <td className="table-cell font-mono text-sm">{enforcer.badge_number}</td>
                            <td className="table-cell">{enforcer.total_violations}</td>
                            <td className="table-cell">${enforcer.total_fines.toFixed(2)}</td>
                            <td className="table-cell">${enforcer.collected_fines.toFixed(2)}</td>
                            <td className="table-cell">
                              <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                                enforcer.collection_rate >= 80 ? 'bg-green-100 text-green-800' :
                                enforcer.collection_rate >= 60 ? 'bg-yellow-100 text-yellow-800' :
                                'bg-red-100 text-red-800'
                              }`}>
                                {enforcer.collection_rate}%
                              </span>
                            </td>
                            <td className="table-cell">{enforcer.today_violations}</td>
                            <td className="table-cell">{enforcer.month_violations}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

