                  <path fill="#FF3D00" d="M6.3 14.7l6.6 4.8C14.6 15.3 18.9 12 24 12c3.2 0 6.1 1.2 8.3 3.2l5.7-5.7C34.5 3.9 29.6 2 24 2 15 2 7.5 7.2 4.3 14.7z"/>
                  <path fill="#4CAF50" d="M24 46c5.2 0 10-1.9 13.6-5.1l-6.3-5.2c-2.1 1.5-4.8 2.3-7.3 2.3-5.2 0-9.6-3.4-11.3-8L6.2 34.8C9.4 42.3 16.1 46 24 46z"/>
                  <path fill="#1976D2" d="M43.6 20.5H42V20H24v8h11.3c-1.3 3.8-5.9 7-11.3 7-7 0-12.7-5.7-12.7-12.7S17 9.6 24 9.6c3.2 0 6.1 1.2 8.3 3.2l5.7-5.7C34.5 3.9 29.6 2 24 2c-8.2 0-15.2 5.3-17.7 12.7z"/>
                </svg>
