import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { reportsAPI } from '../../services/api';
import { 
  Download, 
  FileText, 
  Users, 
  TrendingUp, 
  Calendar,
  Filter,
  BarChart3,
  PieChart,
  Table
} from 'lucide-react';
import LoadingSpinner from '../../components/LoadingSpinner';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart as RechartsPieChart,
  Pie,
  Cell,
  LineChart,
  Line
} from 'recharts';

const COLORS = ['#3b82f6', '#ef4444', '#f59e0b', '#22c55e', '#8b5cf6', '#ec4899'];

const ReportCard = ({ title, description, icon: Icon, onClick, color = 'primary' }) => (
  <div 
    className={`card cursor-pointer hover:shadow-lg transition-shadow border-l-4 border-l-${color}-500`}
    onClick={onClick}
  >
    <div className="card-body">
      <div className="flex items-center">
        <div className={`flex-shrink-0 p-3 rounded-md bg-${color}-100`}>
          <Icon className={`h-6 w-6 text-${color}-600`} />
        </div>
        <div className="ml-4">
          <h3 className="text-lg font-medium text-gray-900">{title}</h3>
          <p className="text-sm text-gray-500">{description}</p>
        </div>
      </div>
    </div>
  </div>
);

const DateRangePicker = ({ startDate, endDate, onStartDateChange, onEndDateChange }) => (
  <div className="flex gap-4 items-center">
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">Start Date</label>
      <input
        type="date"
        value={startDate}
        onChange={(e) => onStartDateChange(e.target.value)}
        className="input"
      />
    </div>
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">End Date</label>
      <input
        type="date"
        value={endDate}
        onChange={(e) => onEndDateChange(e.target.value)}
        className="input"
      />
    </div>
  </div>
);

const ExportButtons = ({ onExport, reportType, isLoading }) => (
  <div className="flex gap-2">
    <button
      onClick={() => onExport('json')}
      disabled={isLoading}
      className="btn-secondary flex items-center gap-2"
    >
      <FileText className="h-4 w-4" />
      Export JSON
    </button>
    <button
      onClick={() => onExport('csv')}
      disabled={isLoading}
      className="btn-primary flex items-center gap-2"
    >
      <Download className="h-4 w-4" />
      Export CSV
    </button>
  </div>
);

const ViolationsReport = ({ filters, setFilters }) => {
  const [reportData, setReportData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const generateReport = async () => {
    if (!filters.startDate || !filters.endDate) {
      alert('Please select both start and end dates');
      return;
    }
    
    setIsLoading(true);
    try {
      const response = await reportsAPI.getViolationsReport(filters);
      setReportData(response.data);
    } catch (error) {
      console.error('Failed to generate report:', error);
      alert('Failed to generate report');
    } finally {
      setIsLoading(false);
    }
  };

  const exportReport = (format) => {
    if (!reportData) return;
    
    if (format === 'json') {
      const dataStr = JSON.stringify(reportData, null, 2);
      const dataBlob = new Blob([dataStr], { type: 'application/json' });
      const url = URL.createObjectURL(dataBlob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `violations-report-${filters.startDate}-to-${filters.endDate}.json`;
      link.click();
    } else if (format === 'csv') {
      // Convert to CSV
      const headers = ['Date', 'Violations', 'Fines Collected', 'Status'];
      const csvContent = [
        headers.join(','),
        ...reportData.dailyStats.map(stat => 
          `${stat.date},${stat.violations},${stat.fines},${stat.status}`
        )
      ].join('\n');
      
      const blob = new Blob([csvContent], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `violations-report-${filters.startDate}-to-${filters.endDate}.csv`;
      link.click();
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-xl font-bold text-gray-900">Violations Report</h2>
          <p className="text-gray-600">Generate detailed reports on traffic violations</p>
        </div>
        <ExportButtons onExport={exportReport} reportType="violations" isLoading={isLoading} />
      </div>

      <div className="card">
        <div className="card-body">
          <div className="flex gap-4 items-end mb-4">
            <DateRangePicker
              startDate={filters.startDate}
              endDate={filters.endDate}
              onStartDateChange={(date) => setFilters(prev => ({ ...prev, startDate: date }))}
              onEndDateChange={(date) => setFilters(prev => ({ ...prev, endDate: date }))}
            />
            <button
              onClick={generateReport}
              disabled={isLoading}
              className="btn-primary"
            >
              {isLoading ? <LoadingSpinner size="sm" /> : 'Generate Report'}
            </button>
          </div>

          {reportData && (
            <div className="space-y-6">
              {/* Summary Stats */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-blue-600">Total Violations</h4>
                  <p className="text-2xl font-bold text-blue-900">{reportData.summary.totalViolations}</p>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-green-600">Total Fines</h4>
                  <p className="text-2xl font-bold text-green-900">${reportData.summary.totalFines}</p>
                </div>
                <div className="bg-yellow-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-yellow-600">Pending</h4>
                  <p className="text-2xl font-bold text-yellow-900">{reportData.summary.pending}</p>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-purple-600">Paid</h4>
                  <p className="text-2xl font-bold text-purple-900">{reportData.summary.paid}</p>
                </div>
              </div>

              {/* Charts */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <div className="card">
                  <div className="card-header">
                    <h4 className="text-lg font-medium">Daily Violations Trend</h4>
                  </div>
                  <div className="card-body">
                    <div className="h-64">
                      <ResponsiveContainer width="100%" height="100%">
                        <LineChart data={reportData.dailyStats}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="date" />
                          <YAxis />
                          <Tooltip />
                          <Line type="monotone" dataKey="violations" stroke="#3b82f6" strokeWidth={2} />
                        </LineChart>
                      </ResponsiveContainer>
                    </div>
                  </div>
                </div>

                <div className="card">
                  <div className="card-header">
                    <h4 className="text-lg font-medium">Violations by Type</h4>
                  </div>
                  <div className="card-body">
                    <div className="h-64">
                      <ResponsiveContainer width="100%" height="100%">
                        <RechartsPieChart>
                          <Pie
                            data={reportData.violationsByType}
                            cx="50%"
                            cy="50%"
                            labelLine={false}
                            label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                            outerRadius={80}
                            fill="#8884d8"
                            dataKey="count"
                          >
                            {reportData.violationsByType.map((entry, index) => (
                              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                            ))}
                          </Pie>
                          <Tooltip />
                        </RechartsPieChart>
                      </ResponsiveContainer>
                    </div>
                  </div>
                </div>
              </div>

              {/* Detailed Table */}
              <div className="card">
                <div className="card-header">
                  <h4 className="text-lg font-medium">Daily Breakdown</h4>
                </div>
                <div className="card-body">
                  <div className="overflow-x-auto">
                    <table className="table">
                      <thead className="table-header">
                        <tr>
                          <th className="table-header-cell">Date</th>
                          <th className="table-header-cell">Violations</th>
                          <th className="table-header-cell">Fines Collected</th>
                          <th className="table-header-cell">Pending</th>
                          <th className="table-header-cell">Paid</th>
                        </tr>
                      </thead>
                      <tbody className="table-body">
                        {reportData.dailyStats.map((stat, index) => (
                          <tr key={index} className="table-row">
                            <td className="table-cell">{stat.date}</td>
                            <td className="table-cell">{stat.violations}</td>
                            <td className="table-cell">${stat.fines}</td>
                            <td className="table-cell">{stat.pending}</td>
                            <td className="table-cell">{stat.paid}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const EnforcersReport = ({ filters, setFilters }) => {
  const [reportData, setReportData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const generateReport = async () => {
    if (!filters.startDate || !filters.endDate) {
      alert('Please select both start and end dates');
      return;
    }
    
    setIsLoading(true);
    try {
      const response = await reportsAPI.getEnforcersReport(filters);
      setReportData(response.data);
    } catch (error) {
      console.error('Failed to generate report:', error);
      alert('Failed to generate report');
    } finally {
      setIsLoading(false);
    }
  };

  const exportReport = (format) => {
    if (!reportData) return;
    
    if (format === 'json') {
      const dataStr = JSON.stringify(reportData, null, 2);
      const dataBlob = new Blob([dataStr], { type: 'application/json' });
      const url = URL.createObjectURL(dataBlob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `enforcers-report-${filters.startDate}-to-${filters.endDate}.json`;
      link.click();
    } else if (format === 'csv') {
      const headers = ['Enforcer', 'Violations Issued', 'Fines Collected', 'Performance Score'];
      const csvContent = [
        headers.join(','),
        ...reportData.enforcers.map(enforcer => 
          `${enforcer.name},${enforcer.violations},${enforcer.fines},${enforcer.performanceScore}`
        )
      ].join('\n');
      
      const blob = new Blob([csvContent], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `enforcers-report-${filters.startDate}-to-${filters.endDate}.csv`;
      link.click();
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-xl font-bold text-gray-900">Enforcers Performance Report</h2>
          <p className="text-gray-600">Track enforcer performance and productivity</p>
        </div>
        <ExportButtons onExport={exportReport} reportType="enforcers" isLoading={isLoading} />
      </div>

      <div className="card">
        <div className="card-body">
          <div className="flex gap-4 items-end mb-4">
            <DateRangePicker
              startDate={filters.startDate}
              endDate={filters.endDate}
              onStartDateChange={(date) => setFilters(prev => ({ ...prev, startDate: date }))}
              onEndDateChange={(date) => setFilters(prev => ({ ...prev, endDate: date }))}
            />
            <button
              onClick={generateReport}
              disabled={isLoading}
              className="btn-primary"
            >
              {isLoading ? <LoadingSpinner size="sm" /> : 'Generate Report'}
            </button>
          </div>

          {reportData && (
            <div className="space-y-6">
              {/* Performance Chart */}
              <div className="card">
                <div className="card-header">
                  <h4 className="text-lg font-medium">Enforcer Performance Comparison</h4>
                </div>
                <div className="card-body">
                  <div className="h-64">
                    <ResponsiveContainer width="100%" height="100%">
                      <BarChart data={reportData.enforcers}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip />
                        <Bar dataKey="violations" fill="#3b82f6" name="Violations Issued" />
                        <Bar dataKey="fines" fill="#10b981" name="Fines Collected" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>
                </div>
              </div>

              {/* Performance Table */}
              <div className="card">
                <div className="card-header">
                  <h4 className="text-lg font-medium">Detailed Performance</h4>
                </div>
                <div className="card-body">
                  <div className="overflow-x-auto">
                    <table className="table">
                      <thead className="table-header">
                        <tr>
                          <th className="table-header-cell">Enforcer</th>
                          <th className="table-header-cell">Violations Issued</th>
                          <th className="table-header-cell">Fines Collected</th>
                          <th className="table-header-cell">Performance Score</th>
                          <th className="table-header-cell">Status</th>
                        </tr>
                      </thead>
                      <tbody className="table-body">
                        {reportData.enforcers.map((enforcer, index) => (
                          <tr key={index} className="table-row">
                            <td className="table-cell font-medium">{enforcer.name}</td>
                            <td className="table-cell">{enforcer.violations}</td>
                            <td className="table-cell">${enforcer.fines}</td>
                            <td className="table-cell">
                              <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                                enforcer.performanceScore >= 80 ? 'bg-green-100 text-green-800' :
                                enforcer.performanceScore >= 60 ? 'bg-yellow-100 text-yellow-800' :
                                'bg-red-100 text-red-800'
                              }`}>
                                {enforcer.performanceScore}%
                              </span>
                            </td>
                            <td className="table-cell">
                              <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                                enforcer.isActive ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                              }`}>
                                {enforcer.isActive ? 'Active' : 'Inactive'}
                              </span>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const Reports = () => {
  const [activeReport, setActiveReport] = useState(null);
  const [filters, setFilters] = useState({
    startDate: '',
    endDate: '',
  });

  const reportTypes = [
    {
      id: 'violations',
      title: 'Violations Report',
      description: 'Comprehensive analysis of traffic violations with trends and statistics',
      icon: FileText,
      color: 'primary'
    },
    {
      id: 'enforcers',
      title: 'Enforcers Performance',
      description: 'Track enforcer productivity and performance metrics',
      icon: Users,
      color: 'success'
    },
    {
      id: 'daily-summary',
      title: 'Daily Summary',
      description: 'Quick overview of daily activities and key metrics',
      icon: Calendar,
      color: 'warning'
    },
    {
      id: 'monthly',
      title: 'Monthly Analysis',
      description: 'Monthly trends and comparative analysis',
      icon: TrendingUp,
      color: 'info'
    }
  ];

  const handleReportSelect = (reportType) => {
    setActiveReport(reportType);
    // Set default date range to last 30 days
    const endDate = new Date().toISOString().split('T')[0];
    const startDate = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];
    setFilters({ startDate, endDate });
  };

  const renderActiveReport = () => {
    switch (activeReport) {
      case 'violations':
        return <ViolationsReport filters={filters} setFilters={setFilters} />;
      case 'enforcers':
        return <EnforcersReport filters={filters} setFilters={setFilters} />;
      case 'daily-summary':
        return (
          <div className="text-center py-12">
            <p className="text-gray-500">Daily Summary report coming soon...</p>
          </div>
        );
      case 'monthly':
        return (
          <div className="text-center py-12">
            <p className="text-gray-500">Monthly Analysis report coming soon...</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Reports & Analytics</h1>
        <p className="mt-1 text-sm text-gray-500">
          Generate comprehensive reports and analyze system performance
        </p>
      </div>

      {!activeReport ? (
        /* Report Selection Grid */
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-6">
          {reportTypes.map((report) => (
            <ReportCard
              key={report.id}
              title={report.title}
              description={report.description}
              icon={report.icon}
              color={report.color}
              onClick={() => handleReportSelect(report.id)}
            />
          ))}
        </div>
      ) : (
        /* Active Report View */
        <div>
          <div className="mb-6">
            <button
              onClick={() => setActiveReport(null)}
              className="btn-secondary flex items-center gap-2"
            >
              <Filter className="h-4 w-4" />
              Back to Reports
            </button>
          </div>
          {renderActiveReport()}
        </div>
      )}
    </div>
  );
};

export default Reports;
