import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';
import Login from './pages/Login';
import AdminDashboard from './pages/admin/Dashboard';
import AdminViolations from './pages/admin/Violations';
import AdminEnforcers from './pages/admin/Enforcers';
import AdminReports from './pages/admin/Reports';
import AdminSettings from './pages/admin/Settings';
import EnforcerDashboard from './pages/enforcer/Dashboard';
import EnforcerViolations from './pages/enforcer/Violations';
import EnforcerProfile from './pages/enforcer/Profile';
import Layout from './components/Layout';
import LoadingSpinner from './components/LoadingSpinner';

function App() {
  const { user, loading } = useAuth();

  if (loading) {
    return <LoadingSpinner />;
  }

  if (!user) {
    return <Login />;
  }

  return (
    <Layout>
      <Routes>
        {user.role === 'admin' ? (
          <>
            <Route path="/" element={<Navigate to="/dashboard" replace />} />
            <Route path="/dashboard" element={<AdminDashboard />} />
            <Route path="/violations" element={<AdminViolations />} />
            <Route path="/enforcers" element={<AdminEnforcers />} />
            <Route path="/reports" element={<AdminReports />} />
            <Route path="/settings" element={<AdminSettings />} />
          </>
        ) : (
          <>
            <Route path="/" element={<Navigate to="/dashboard" replace />} />
            <Route path="/dashboard" element={<EnforcerDashboard />} />
            <Route path="/violations" element={<EnforcerViolations />} />
            <Route path="/profile" element={<EnforcerProfile />} />
          </>
        )}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Layout>
  );
}

export default App;
