        <div>
          <h2 className="text-xl font-bold text-gray-900">Enforcers Performance Report</h2>
          <p className="text-gray-600">Track enforcer productivity and performance metrics</p>
          <div className="mt-2 flex items-center gap-2 text-sm text-gray-500">
            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
            <span>Live Data</span>
            <span>• Last updated: {lastRefresh.toLocaleTimeString()}</span>
          </div>
        </div>
        <div className="flex gap-2">
          <button
            onClick={async () => {
              try {
                const response = await reportsAPI.testDatabase();
                console.log('Database test response:', response.data);
                alert(`Database Status: ${JSON.stringify(response.data.data, null, 2)}`);
              } catch (error) {
                console.error('Database test failed:', error);
                alert(`Database test failed: ${error.message}`);
              }
            }}
            className="btn-secondary text-xs px-3 py-1"
          >
            Test DB
          </button>
          <button
            onClick={generateReport}
            disabled={isLoading}
            className="btn-secondary text-xs px-3 py-1 flex items-center gap-1"
          >
            <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
            </svg>
            Refresh
          </button>
          <ExportButtons onExport={exportReport} reportType="enforcers" isLoading={isLoading} hasData={!!reportData} />
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
