                  <p className="text-3xl font-bold text-purple-900">{reportData.summary.collection_rate}%</p>
                  <div className="mt-2 flex items-center text-sm text-purple-600">
                    <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
                    </svg>
                    <span>Success rate</span>
                  </div>
                </div>
              </div>

              {/* Daily Trend Chart */}
              <div className="bg-white rounded-2xl border border-gray-100 shadow-sm overflow-hidden">
                <div className="px-6 py-4 border-b border-gray-100 bg-gradient-to-r from-gray-50 to-gray-100">
                  <h4 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
                    <TrendingUp className="h-5 w-5 text-primary-600" />
                    Daily Violations Trend - {reportData.month_name}
                  </h4>
                  <p className="text-sm text-gray-600 mt-1">Daily violation patterns throughout the month</p>
                </div>
                <div className="p-6">
                  <div className="h-64">
                    <ResponsiveContainer width="100%" height="100%">
                      <AreaChart data={reportData.daily_breakdown}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#f1f5f9" />
                        <XAxis 
                          dataKey="date" 
                          tick={{ fontSize: 12, fill: '#64748b' }}
                          axisLine={{ stroke: '#e2e8f0' }}
                        />
                        <YAxis 
                          tick={{ fontSize: 12, fill: '#64748b' }}
                          axisLine={{ stroke: '#e2e8f0' }}
                        />
                        <Tooltip 
                          contentStyle={{
                            backgroundColor: 'white',
                            border: '1px solid #e2e8f0',
                            borderRadius: '8px',
                            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                          }}
                        />
                        <Area 
                          type="monotone" 
                          dataKey="violations_count" 
                          stroke="#3b82f6" 
                          fill="#3b82f6" 
                          fillOpacity={0.3} 
                        />
                      </AreaChart>
                    </ResponsiveContainer>
                  </div>
                </div>
              </div>

              {/* Status Breakdown */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <div className="bg-white rounded-2xl border border-gray-100 shadow-sm overflow-hidden">
                  <div className="px-6 py-4 border-b border-gray-100 bg-gradient-to-r from-gray-50 to-gray-100">
                    <h4 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
                      <PieChart className="h-5 w-5 text-primary-600" />
                      Violations by Status
                    </h4>
                    <p className="text-sm text-gray-600 mt-1">Payment status distribution</p>
                  </div>
                  <div className="p-6">
                    <div className="h-64">
                      <ResponsiveContainer width="100%" height="100%">
                        <RechartsPieChart>
                          <Pie
                            data={reportData.violations_by_status}
                            cx="50%"
                            cy="50%"
                            labelLine={false}
                            label={({ status, count }) => `${status}: ${count}`}
                            outerRadius={80}
                            fill="#8884d8"
                            dataKey="count"
                          >
                            {reportData.violations_by_status.map((entry, index) => (
                              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                            ))}
                          </Pie>
                          <Tooltip />
                        </RechartsPieChart>
                      </ResponsiveContainer>
                    </div>
                  </div>
                </div>

                <div className="bg-white rounded-2xl border border-gray-100 shadow-sm overflow-hidden">
                  <div className="px-6 py-4 border-b border-gray-100 bg-gradient-to-r from-gray-50 to-gray-100">
                    <h4 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
                      <Table className="h-5 w-5 text-primary-600" />
                      Monthly Summary
                    </h4>
                    <p className="text-sm text-gray-600 mt-1">Key performance indicators</p>
                  </div>
                  <div className="p-6">
                    <div className="space-y-4">
                      <div className="flex justify-between items-center p-4 bg-gradient-to-r from-blue-50 to-blue-100 rounded-xl border border-blue-200">
                        <span className="text-sm font-semibold text-blue-700 uppercase tracking-wide">Average Daily Violations</span>
                        <span className="text-2xl font-bold text-blue-900">{reportData.summary.avg_daily_violations}</span>
                      </div>
                      <div className="flex justify-between items-center p-4 bg-gradient-to-r from-green-50 to-green-100 rounded-xl border border-green-200">
                        <span className="text-sm font-semibold text-green-700 uppercase tracking-wide">Days with Violations</span>
                        <span className="text-2xl font-bold text-green-900">{reportData.daily_breakdown.filter(d => d.violations_count > 0).length}</span>
                      </div>
                      <div className="flex justify-between items-center p-4 bg-gradient-to-r from-purple-50 to-purple-100 rounded-xl border border-purple-200">
                        <span className="text-sm font-semibold text-purple-700 uppercase tracking-wide">Peak Day</span>
                        <span className="text-2xl font-bold text-purple-900">
                          {reportData.daily_breakdown.reduce((max, day) => 
                            day.violations_count > max.violations_count ? day : max
                          , { violations_count: 0 }).date}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const Reports = () => {
  const [activeReport, setActiveReport] = useState(null);
  const [filters, setFilters] = useState({
    startDate: '',
    endDate: '',
    date: new Date().toISOString().split('T')[0],
    year: new Date().getFullYear(),
    month: new Date().getMonth() + 1
  });
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [isAutoRefresh, setIsAutoRefresh] = useState(true);

  const reportTypes = [
    {
      id: 'violations',
      title: 'Violations Report',
      description: 'Comprehensive analysis of traffic violations with trends and statistics',
      icon: FileText,
      color: 'primary'
    },
    {
      id: 'enforcers',
      title: 'Enforcers Performance',
      description: 'Track enforcer productivity and performance metrics',
      icon: Users,
      color: 'success'
    },
    {
      id: 'daily-summary',
      title: 'Daily Summary',
      description: 'Quick overview of daily activities and key metrics',
      icon: Calendar,
      color: 'warning'
    },
    {
      id: 'monthly',
      title: 'Monthly Analysis',
      description: 'Monthly trends and comparative analysis',
      icon: TrendingUp,
      color: 'info'
    }
  ];

  const handleReportSelect = (reportType) => {
    setActiveReport(reportType);
    // Set default date range to last 30 days for date-based reports
    if (reportType === 'violations' || reportType === 'enforcers') {
      const endDate = new Date().toISOString().split('T')[0];
      const startDate = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];
      setFilters(prev => ({ ...prev, startDate, endDate }));
    }
  };

  // Auto-refresh function
  const refreshData = useCallback(() => {
    setLastUpdate(new Date());
    toast.success('Data refreshed successfully!', { duration: 2000 });
    // Force re-render of active report
    if (activeReport) {
      // This will trigger the report components to refresh their data
      const currentReport = activeReport;
      setActiveReport(null);
      setTimeout(() => setActiveReport(currentReport), 100);
    }
  }, [activeReport]);

  // Auto-refresh effect
  useEffect(() => {
    if (!isAutoRefresh) return;

    const interval = setInterval(() => {
      refreshData();
      // Check for new violations and show notification
      toast.success('🔄 Auto-refreshing data...', { duration: 1500 });
    }, 30000); // Refresh every 30 seconds

    return () => clearInterval(interval);
  }, [isAutoRefresh, refreshData]);

  const renderActiveReport = () => {
    switch (activeReport) {
      case 'violations':
        return <ViolationsReport filters={filters} setFilters={setFilters} />;
      case 'enforcers':
        return <EnforcersReport filters={filters} setFilters={setFilters} />;
      case 'daily-summary':
        return <DailySummaryReport filters={filters} setFilters={setFilters} />;
      case 'monthly':
        return <MonthlyReport filters={filters} setFilters={setFilters} />;
      default:
        return null;
    }
  };

  return (
    <div className="space-y-8">
      <Toaster position="top-right" />
      {/* Header */}
      <div className="bg-gradient-to-r from-primary-50 to-blue-50 rounded-2xl p-8 border border-primary-100">
        <div className="max-w-4xl">
          <h1 className="text-4xl font-bold text-gray-900 mb-3">Reports & Analytics</h1>
          <p className="text-lg text-gray-600 leading-relaxed">
            Generate comprehensive reports and analyze system performance with detailed insights and visualizations
          </p>
          <div className="mt-4 flex items-center justify-between">
            <div className="flex items-center gap-2 text-sm text-primary-600">
              <div className="w-2 h-2 bg-primary-500 rounded-full animate-pulse"></div>
              <span>Real-time data updates</span>
              <span className="text-xs text-gray-500">• Last updated: {lastUpdate.toLocaleTimeString()}</span>
              <span className="text-xs text-green-600">• Auto-refresh: {isAutoRefresh ? 'ON' : 'OFF'}</span>
            </div>
            <div className="flex items-center gap-3">
              <button
                onClick={refreshData}
                className="btn-secondary text-sm px-3 py-1 flex items-center gap-2"
              >
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                </svg>
                Refresh Now
              </button>
              <label className="flex items-center gap-2 text-sm text-gray-600">
                <input
                  type="checkbox"
                  checked={isAutoRefresh}
                  onChange={(e) => setIsAutoRefresh(e.target.checked)}
                  className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                />
                Auto-refresh
              </label>
            </div>
          </div>
        </div>
      </div>

      {!activeReport ? (
        /* Report Selection Grid */
        <div className="space-y-6">
          <div className="text-center mb-8">
            <h2 className="text-2xl font-semibold text-gray-800 mb-2">Choose Your Report Type</h2>
            <p className="text-gray-500">Select from our comprehensive suite of analytical reports</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {reportTypes.map((report) => (
            <ReportCard
              key={report.id}
              title={report.title}
              description={report.description}
              icon={report.icon}
              color={report.color}
              onClick={() => handleReportSelect(report.id)}
            />
          ))}
          </div>
        </div>
      ) : (
        /* Active Report View */
        <div className="space-y-6">
          <div className="flex items-center justify-between bg-white rounded-xl p-6 shadow-sm border border-gray-100">
            <div className="flex items-center gap-4">
            <button
              onClick={() => setActiveReport(null)}
                className="btn-secondary flex items-center gap-2 hover:bg-gray-50 transition-colors"
            >
                <ArrowLeft className="h-4 w-4" />
              Back to Reports
            </button>
              <div className="h-6 w-px bg-gray-200"></div>
              <div>
                <h2 className="text-xl font-semibold text-gray-900">
                  {reportTypes.find(r => r.id === activeReport)?.title}
                </h2>
                <p className="text-sm text-gray-500">
                  {reportTypes.find(r => r.id === activeReport)?.description}
                </p>
              </div>
            </div>
            <div className="flex items-center gap-2 text-sm text-gray-500">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span>Live Data</span>
              <span className="text-xs">• Updated: {lastUpdate.toLocaleTimeString()}</span>
            </div>
          </div>
          {renderActiveReport()}
        </div>
      )}
    </div>
  );
};

export default Reports;
