import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { reportsAPI } from '../../services/api';
import { 
  Download, 
  FileText, 
  Users, 
  TrendingUp, 
  Calendar,
  Filter,
  BarChart3,
  PieChart,
  Table,
  ArrowLeft,
  AlertCircle,
  CheckCircle,
  Clock
} from 'lucide-react';
import LoadingSpinner from '../../components/LoadingSpinner';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart as RechartsPieChart,
  Pie,
  Cell,
  LineChart,
  Line,
  Area,
  AreaChart
} from 'recharts';

const COLORS = ['#3b82f6', '#ef4444', '#f59e0b', '#22c55e', '#8b5cf6', '#ec4899', '#06b6d4', '#84cc16'];

const ReportCard = ({ title, description, icon: Icon, onClick, color = 'primary' }) => (
  <div 
    className={`group relative overflow-hidden bg-white rounded-2xl border border-gray-100 shadow-sm hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 cursor-pointer`}
    onClick={onClick}
  >
    {/* Background gradient overlay */}
    <div className={`absolute inset-0 bg-gradient-to-br from-${color}-50/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300`}></div>
    
    {/* Content */}
    <div className="relative p-8">
      <div className="flex items-start gap-6">
        {/* Icon container */}
        <div className={`flex-shrink-0 p-4 rounded-2xl bg-gradient-to-br from-${color}-100 to-${color}-200 group-hover:from-${color}-200 group-hover:to-${color}-300 transition-all duration-300`}>
          <Icon className={`h-8 w-8 text-${color}-600 group-hover:text-${color}-700 transition-colors duration-300`} />
        </div>
        
        {/* Text content */}
        <div className="flex-1 min-w-0">
          <h3 className="text-xl font-semibold text-gray-900 mb-3 group-hover:text-gray-800 transition-colors duration-300">
            {title}
          </h3>
          <p className="text-gray-600 leading-relaxed group-hover:text-gray-700 transition-colors duration-300">
            {description}
          </p>
        </div>
        
        {/* Arrow indicator */}
        <div className="flex-shrink-0 opacity-0 group-hover:opacity-100 transform translate-x-2 group-hover:translate-x-0 transition-all duration-300">
          <div className={`p-2 rounded-full bg-${color}-100 text-${color}-600`}>
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </div>
        </div>
      </div>
      
      {/* Bottom accent line */}
      <div className={`absolute bottom-0 left-0 right-0 h-1 bg-gradient-to-r from-${color}-400 to-${color}-600 transform scale-x-0 group-hover:scale-x-100 transition-transform duration-500 origin-left`}></div>
    </div>
  </div>
);

const DateRangePicker = ({ startDate, endDate, onStartDateChange, onEndDateChange }) => (
  <div className="flex gap-4 items-center">
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">Start Date</label>
      <input
        type="date"
        value={startDate}
        onChange={(e) => onStartDateChange(e.target.value)}
        className="input"
        max={endDate || undefined}
      />
    </div>
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">End Date</label>
      <input
        type="date"
        value={endDate}
        onChange={(e) => onEndDateChange(e.target.value)}
        className="input"
        min={startDate || undefined}
      />
    </div>
  </div>
);

const ExportButtons = ({ onExport, reportType, isLoading, hasData }) => (
  <div className="flex gap-2">
    <button
      onClick={() => onExport('json')}
      disabled={isLoading || !hasData}
      className="btn-secondary flex items-center gap-2 disabled:opacity-50 disabled:cursor-not-allowed"
    >
      <FileText className="h-4 w-4" />
      Export JSON
    </button>
    <button
      onClick={() => onExport('csv')}
      disabled={isLoading || !hasData}
      className="btn-primary flex items-center gap-2 disabled:opacity-50 disabled:cursor-not-allowed"
    >
      <Download className="h-4 w-4" />
      Export CSV
    </button>
  </div>
);

const ViolationsReport = ({ filters, setFilters }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [reportData, setReportData] = useState(null);
  const [error, setError] = useState(null);

  const generateReport = async () => {
    if (!filters.startDate || !filters.endDate) {
      setError('Please select both start and end dates');
      return;
    }
    
    setIsLoading(true);
    setError(null);
    try {
      const response = await reportsAPI.getViolationsReport({
        start_date: filters.startDate,
        end_date: filters.endDate
      });
      
      if (response.data.success) {
        setReportData(response.data.data);
      } else {
        setError('Failed to generate report');
      }
    } catch (error) {
      console.error('Failed to generate report:', error);
      setError(error.response?.data?.error || 'Failed to generate report');
    } finally {
      setIsLoading(false);
    }
  };

  const exportReport = (format) => {
    if (!reportData) return;
    
    if (format === 'json') {
      const dataStr = JSON.stringify(reportData, null, 2);
      const dataBlob = new Blob([dataStr], { type: 'application/json' });
      const url = URL.createObjectURL(dataBlob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `violations-report-${filters.startDate}-to-${filters.endDate}.json`;
      link.click();
      URL.revokeObjectURL(url);
    } else if (format === 'csv') {
      // Use server-side CSV export
      const params = new URLSearchParams({
        start_date: filters.startDate,
        end_date: filters.endDate,
        format: 'csv'
      });
      
      const url = `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/reports/violations?${params}`;
      const link = document.createElement('a');
      link.href = url;
      link.download = `violations-report-${filters.startDate}-to-${filters.endDate}.csv`;
      link.click();
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-xl font-bold text-gray-900">Violations Report</h2>
          <p className="text-gray-600">Generate detailed reports on traffic violations</p>
        </div>
        <ExportButtons onExport={exportReport} reportType="violations" isLoading={isLoading} hasData={!!reportData} />
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <div className="flex">
            <AlertCircle className="h-5 w-5 text-red-400" />
            <div className="ml-3">
              <p className="text-sm text-red-800">{error}</p>
            </div>
          </div>
        </div>
      )}

      <div className="card">
        <div className="card-body">
          <div className="flex gap-4 items-end mb-4">
            <DateRangePicker
              startDate={filters.startDate}
              endDate={filters.endDate}
              onStartDateChange={(date) => setFilters(prev => ({ ...prev, startDate: date }))}
              onEndDateChange={(date) => setFilters(prev => ({ ...prev, endDate: date }))}
            />
            <button
              onClick={generateReport}
              disabled={isLoading}
              className="btn-primary"
            >
              {isLoading ? <LoadingSpinner size="sm" /> : 'Generate Report'}
            </button>
          </div>

          {reportData && (
            <div className="space-y-6">
              {/* Summary Stats */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-blue-600">Total Violations</h4>
                  <p className="text-2xl font-bold text-blue-900">{reportData.summary.total_violations}</p>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-green-600">Total Fines</h4>
                  <p className="text-2xl font-bold text-green-900">${reportData.summary.total_fines.toFixed(2)}</p>
                </div>
                <div className="bg-yellow-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-yellow-600">Pending Fines</h4>
                  <p className="text-2xl font-bold text-yellow-900">${reportData.summary.pending_fines.toFixed(2)}</p>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-purple-600">Collection Rate</h4>
                  <p className="text-2xl font-bold text-purple-900">{reportData.summary.collection_rate}%</p>
                </div>
              </div>

              {/* Violations Table */}
              <div className="card">
                <div className="card-header">
                  <h4 className="text-lg font-medium">Violations Details</h4>
                </div>
                <div className="card-body">
                  <div className="overflow-x-auto">
                    <table className="table">
                      <thead className="table-header">
                        <tr>
                          <th className="table-header-cell">Violation #</th>
                          <th className="table-header-cell">Violator</th>
                          <th className="table-header-cell">Vehicle</th>
                          <th className="table-header-cell">Type</th>
                          <th className="table-header-cell">Fine</th>
                          <th className="table-header-cell">Status</th>
                          <th className="table-header-cell">Enforcer</th>
                          <th className="table-header-cell">Date</th>
                        </tr>
                      </thead>
                      <tbody className="table-body">
                        {reportData.violations.map((violation, index) => (
                          <tr key={index} className="table-row">
                            <td className="table-cell font-mono text-sm">{violation.violation_number}</td>
                            <td className="table-cell font-medium">{violation.violator_name}</td>
                            <td className="table-cell">{violation.vehicle_plate || '-'}</td>
                            <td className="table-cell">
                              <span className="inline-flex px-2 py-1 text-xs font-semibold rounded-full bg-blue-100 text-blue-800">
                                {violation.violation_type}
                              </span>
                            </td>
                            <td className="table-cell font-medium">${parseFloat(violation.fine_amount).toFixed(2)}</td>
                            <td className="table-cell">
                              <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                                violation.status === 'paid' ? 'bg-green-100 text-green-800' :
                                violation.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                                'bg-red-100 text-red-800'
                              }`}>
                                {violation.status}
                              </span>
                            </td>
                            <td className="table-cell">{violation.enforcer_name}</td>
                            <td className="table-cell text-sm text-gray-500">
                              {new Date(violation.created_at).toLocaleDateString()}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const EnforcersReport = ({ filters, setFilters }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [reportData, setReportData] = useState(null);
  const [error, setError] = useState(null);

  const generateReport = async () => {
    if (!filters.startDate || !filters.endDate) {
      setError('Please select both start and end dates');
      return;
    }
    
    setIsLoading(true);
    setError(null);
    try {
      console.log('Generating enforcers report with filters:', filters);
      const response = await reportsAPI.getEnforcersReport({
        start_date: filters.startDate,
        end_date: filters.endDate
      });
      
      console.log('Enforcers report response:', response);
      
      if (response.data.success) {
        setReportData(response.data.data);
        console.log('Enforcers report data set:', response.data.data);
      } else {
        setError('Failed to generate report');
      }
    } catch (error) {
      console.error('Failed to generate enforcers report:', error);
      console.error('Error response:', error.response);
      console.error('Error message:', error.message);
      
      if (error.response?.status === 500) {
        setError('Server error: Database connection or query failed');
      } else if (error.response?.status === 404) {
        setError('Report endpoint not found');
      } else if (error.response?.status === 401) {
        setError('Authentication failed. Please login again.');
      } else if (error.code === 'ERR_NETWORK') {
        setError('Network error: Cannot connect to server');
      } else {
        setError(error.response?.data?.error || error.message || 'Failed to generate report');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const exportReport = (format) => {
    if (!reportData) return;
    
    if (format === 'json') {
      const dataStr = JSON.stringify(reportData, null, 2);
      const dataBlob = new Blob([dataStr], { type: 'application/json' });
      const url = URL.createObjectURL(dataBlob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `enforcers-report-${filters.startDate}-to-${filters.endDate}.json`;
      link.click();
      URL.revokeObjectURL(url);
    } else if (format === 'csv') {
      const params = new URLSearchParams({
        start_date: filters.startDate,
        end_date: filters.endDate,
        format: 'csv'
      });
      
      const url = `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/reports/enforcers?${params}`;
      const link = document.createElement('a');
      link.href = url;
      link.download = `enforcers-report-${filters.startDate}-to-${filters.endDate}.csv`;
      link.click();
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-xl font-bold text-gray-900">Enforcers Performance Report</h2>
          <p className="text-gray-600">Track enforcer productivity and performance metrics</p>
        </div>
        <div className="flex gap-2">
          <button
            onClick={async () => {
              try {
                const response = await reportsAPI.testDatabase();
                console.log('Database test response:', response.data);
                alert(`Database Status: ${JSON.stringify(response.data.data, null, 2)}`);
              } catch (error) {
                console.error('Database test failed:', error);
                alert(`Database test failed: ${error.message}`);
              }
            }}
            className="btn-secondary text-xs px-3 py-1"
          >
            Test DB
          </button>
          <ExportButtons onExport={exportReport} reportType="enforcers" isLoading={isLoading} hasData={!!reportData} />
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <div className="flex">
            <AlertCircle className="h-5 w-5 text-red-400" />
            <div className="ml-3">
              <p className="text-sm text-red-800">{error}</p>
            </div>
          </div>
        </div>
      )}

      <div className="card">
        <div className="card-body">
          <div className="flex gap-4 items-end mb-4">
            <DateRangePicker
              startDate={filters.startDate}
              endDate={filters.endDate}
              onStartDateChange={(date) => setFilters(prev => ({ ...prev, startDate: date }))}
              onEndDateChange={(date) => setFilters(prev => ({ ...prev, endDate: date }))}
            />
            <button
              onClick={generateReport}
              disabled={isLoading}
              className="btn-primary"
            >
              {isLoading ? <LoadingSpinner size="sm" /> : 'Generate Report'}
            </button>
          </div>

          {reportData && (
            <div className="space-y-6">
              {/* Summary Stats */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-blue-600">Total Enforcers</h4>
                  <p className="text-2xl font-bold text-blue-900">{reportData.summary?.total_enforcers || 0}</p>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-green-600">Total Violations</h4>
                  <p className="text-2xl font-bold text-green-900">{reportData.summary?.total_violations || 0}</p>
                </div>
                <div className="bg-yellow-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-yellow-600">Total Fines</h4>
                  <p className="text-2xl font-bold text-yellow-900">${(reportData.summary?.total_fines || 0).toFixed(2)}</p>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-purple-600">Avg Collection Rate</h4>
                  <p className="text-2xl font-bold text-purple-900">{reportData.summary?.avg_collection_rate || 0}%</p>
                </div>
              </div>

              {/* Check if there are enforcers with data */}
              {(!reportData.enforcers || reportData.enforcers.length === 0) ? (
                <div className="text-center py-12">
                  <div className="text-gray-500">
                    <Users className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                    <h3 className="text-lg font-medium text-gray-900 mb-2">No Enforcer Data Found</h3>
                    <p className="text-gray-500 mb-4">
                      {reportData.message || 'No enforcers found for the selected date range, or no violations have been recorded yet.'}
                    </p>
                    <div className="text-sm text-gray-400">
                      <p>• Try selecting a different date range</p>
                      <p>• Check if there are any enforcers in the system</p>
                      <p>• Verify that violations exist for the selected period</p>
                    </div>
                  </div>
                </div>
              ) : (
                <>
                  {/* Performance Chart */}
                  <div className="card">
                    <div className="card-header">
                      <h4 className="text-lg font-medium">Enforcer Performance Comparison</h4>
                    </div>
                    <div className="card-body">
                      <div className="h-64">
                        <ResponsiveContainer width="100%" height="100%">
                          <BarChart data={reportData.enforcers}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="full_name" />
                            <YAxis />
                            <Tooltip />
                            <Bar dataKey="total_violations" fill="#3b82f6" name="Violations Issued" />
                            <Bar dataKey="collected_fines" fill="#10b981" name="Fines Collected" />
                          </BarChart>
                        </ResponsiveContainer>
                      </div>
                    </div>
                  </div>

                  {/* Performance Table */}
                  <div className="card">
                    <div className="card-header">
                      <h4 className="text-lg font-medium">Detailed Performance</h4>
                    </div>
                    <div className="card-body">
                      <div className="overflow-x-auto">
                        <table className="table">
                          <thead className="table-header">
                            <tr>
                              <th className="table-header-cell">Enforcer</th>
                              <th className="table-header-cell">Badge #</th>
                              <th className="table-header-cell">Violations</th>
                              <th className="table-header-cell">Total Fines</th>
                              <th className="table-header-cell">Collected</th>
                              <th className="table-header-cell">Collection Rate</th>
                              <th className="table-header-cell">Today</th>
                              <th className="table-header-cell">This Month</th>
                            </tr>
                          </thead>
                          <tbody className="table-body">
                            {reportData.enforcers.map((enforcer, index) => (
                              <tr key={index} className="table-row">
                                <td className="table-cell font-medium">{enforcer.full_name || 'Unknown'}</td>
                                <td className="table-cell font-mono text-sm">{enforcer.badge_number || '-'}</td>
                                <td className="table-cell">{enforcer.total_violations || 0}</td>
                                <td className="table-cell">${(enforcer.total_fines || 0).toFixed(2)}</td>
                                <td className="table-cell">${(enforcer.collected_fines || 0).toFixed(2)}</td>
                                <td className="table-cell">
                                  <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                                    (enforcer.collection_rate || 0) >= 80 ? 'bg-green-100 text-green-800' :
                                    (enforcer.collection_rate || 0) >= 60 ? 'bg-yellow-100 text-yellow-800' :
                                    'bg-red-100 text-red-800'
                                  }`}>
                                    {(enforcer.collection_rate || 0).toFixed(1)}%
                                  </span>
                                </td>
                                <td className="table-cell">{enforcer.today_violations || 0}</td>
                                <td className="table-cell">{enforcer.month_violations || 0}</td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      </div>
                    </div>
                  </div>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const DailySummaryReport = ({ filters, setFilters }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [reportData, setReportData] = useState(null);
  const [error, setError] = useState(null);

  const generateReport = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await reportsAPI.getDailySummary({
        date: filters.date || new Date().toISOString().split('T')[0]
      });
      
      if (response.data.success) {
        setReportData(response.data.data);
      } else {
        setError('Failed to generate report');
      }
    } catch (error) {
      console.error('Failed to generate report:', error);
      setError(error.response?.data?.error || 'Failed to generate report');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-xl font-bold text-gray-900">Daily Summary Report</h2>
          <p className="text-gray-600">Quick overview of daily activities and key metrics</p>
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <div className="flex">
            <AlertCircle className="h-5 w-5 text-red-400" />
            <div className="ml-3">
              <p className="text-sm text-red-800">{error}</p>
            </div>
          </div>
        </div>
      )}

      <div className="card">
        <div className="card-body">
          <div className="flex gap-4 items-end mb-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Date</label>
              <input
                type="date"
                value={filters.date || new Date().toISOString().split('T')[0]}
                onChange={(e) => setFilters(prev => ({ ...prev, date: e.target.value }))}
                className="input"
                max={new Date().toISOString().split('T')[0]}
              />
            </div>
            <button
              onClick={generateReport}
              disabled={isLoading}
              className="btn-primary"
            >
              {isLoading ? <LoadingSpinner size="sm" /> : 'Generate Report'}
            </button>
          </div>

          {reportData && (
            <div className="space-y-6">
              {/* Summary Stats */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-blue-600">Total Violations</h4>
                  <p className="text-2xl font-bold text-blue-900">{reportData.summary.total_violations}</p>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-green-600">Total Fines</h4>
                  <p className="text-2xl font-bold text-green-900">${reportData.summary.total_fines.toFixed(2)}</p>
                </div>
                <div className="bg-yellow-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-yellow-600">Active Enforcers</h4>
                  <p className="text-2xl font-bold text-yellow-900">{reportData.summary.active_enforcers}</p>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-purple-600">Avg per Enforcer</h4>
                  <p className="text-2xl font-bold text-purple-900">{reportData.summary.avg_violations_per_enforcer}</p>
                </div>
              </div>

              {/* Violations by Type Chart */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <div className="card">
                  <div className="card-header">
                    <h4 className="text-lg font-medium">Violations by Type</h4>
                  </div>
                  <div className="card-body">
                    <div className="h-64">
                      <ResponsiveContainer width="100%" height="100%">
                        <RechartsPieChart>
                          <Pie
                            data={reportData.violations_by_type}
                            cx="50%"
                            cy="50%"
                            labelLine={false}
                            label={({ violation_type, count }) => `${violation_type}: ${count}`}
                            outerRadius={80}
                            fill="#8884d8"
                            dataKey="count"
                          >
                            {reportData.violations_by_type.map((entry, index) => (
                              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                            ))}
                          </Pie>
                          <Tooltip />
                        </RechartsPieChart>
                      </ResponsiveContainer>
                    </div>
                  </div>
                </div>

                <div className="card">
                  <div className="card-header">
                    <h4 className="text-lg font-medium">Enforcer Activity</h4>
                  </div>
                  <div className="card-body">
                    <div className="h-64">
                      <ResponsiveContainer width="100%" height="100%">
                        <BarChart data={reportData.violations_by_enforcer}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="full_name" />
                          <YAxis />
                          <Tooltip />
                          <Bar dataKey="violations_count" fill="#3b82f6" name="Violations" />
                        </BarChart>
                      </ResponsiveContainer>
                    </div>
                  </div>
                </div>
              </div>

              {/* Recent Violations */}
              <div className="card">
                <div className="card-header">
                  <h4 className="text-lg font-medium">Recent Violations</h4>
                </div>
                <div className="card-body">
                  <div className="overflow-x-auto">
                    <table className="table">
                      <thead className="table-header">
                        <tr>
                          <th className="table-header-cell">Time</th>
                          <th className="table-header-cell">Violator</th>
                          <th className="table-header-cell">Type</th>
                          <th className="table-header-cell">Fine</th>
                          <th className="table-header-cell">Enforcer</th>
                        </tr>
                      </thead>
                      <tbody className="table-body">
                        {reportData.recent_violations.map((violation, index) => (
                          <tr key={index} className="table-row">
                            <td className="table-cell text-sm text-gray-500">
                              {new Date(violation.created_at).toLocaleTimeString()}
                            </td>
                            <td className="table-cell font-medium">{violation.violator_name}</td>
                            <td className="table-cell">
                              <span className="inline-flex px-2 py-1 text-xs font-semibold rounded-full bg-blue-100 text-blue-800">
                                {violation.violation_type}
                              </span>
                            </td>
                            <td className="table-cell font-medium">${parseFloat(violation.fine_amount).toFixed(2)}</td>
                            <td className="table-cell">{violation.enforcer_name}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const MonthlyReport = ({ filters, setFilters }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [reportData, setReportData] = useState(null);
  const [error, setError] = useState(null);

  const generateReport = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await reportsAPI.getMonthlyReport({
        year: filters.year || new Date().getFullYear(),
        month: filters.month || new Date().getMonth() + 1
      });
      
      if (response.data.success) {
        setReportData(response.data.data);
      } else {
        setError('Failed to generate report');
      }
    } catch (error) {
      console.error('Failed to generate report:', error);
      setError(error.response?.data?.error || 'Failed to generate report');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-xl font-bold text-gray-900">Monthly Analysis Report</h2>
          <p className="text-gray-600">Monthly trends and comparative analysis</p>
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <div className="flex">
            <AlertCircle className="h-5 w-5 text-red-400" />
            <div className="ml-3">
              <p className="text-sm text-red-800">{error}</p>
            </div>
          </div>
        </div>
      )}

      <div className="card">
        <div className="card-body">
          <div className="flex gap-4 items-end mb-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Year</label>
              <select
                value={filters.year || new Date().getFullYear()}
                onChange={(e) => setFilters(prev => ({ ...prev, year: parseInt(e.target.value) }))}
                className="input"
              >
                {Array.from({ length: 5 }, (_, i) => new Date().getFullYear() - i).map(year => (
                  <option key={year} value={year}>{year}</option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Month</label>
              <select
                value={filters.month || new Date().getMonth() + 1}
                onChange={(e) => setFilters(prev => ({ ...prev, month: parseInt(e.target.value) }))}
                className="input"
              >
                {Array.from({ length: 12 }, (_, i) => i + 1).map(month => (
                  <option key={month} value={month}>
                    {new Date(2000, month - 1).toLocaleDateString('en-US', { month: 'long' })}
                  </option>
                ))}
              </select>
            </div>
            <button
              onClick={generateReport}
              disabled={isLoading}
              className="btn-primary"
            >
              {isLoading ? <LoadingSpinner size="sm" /> : 'Generate Report'}
            </button>
          </div>

          {reportData && (
            <div className="space-y-6">
              {/* Summary Stats */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-blue-600">Total Violations</h4>
                  <p className="text-2xl font-bold text-blue-900">{reportData.summary.total_violations}</p>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-green-600">Total Fines</h4>
                  <p className="text-2xl font-bold text-green-900">${reportData.summary.total_fines.toFixed(2)}</p>
                </div>
                <div className="bg-yellow-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-yellow-600">Paid Fines</h4>
                  <p className="text-2xl font-bold text-yellow-900">${reportData.summary.paid_fines.toFixed(2)}</p>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <h4 className="text-sm font-medium text-purple-600">Collection Rate</h4>
                  <p className="text-2xl font-bold text-purple-900">{reportData.summary.collection_rate}%</p>
                </div>
              </div>

              {/* Daily Trend Chart */}
              <div className="card">
                <div className="card-header">
                  <h4 className="text-lg font-medium">Daily Violations Trend - {reportData.month_name}</h4>
                </div>
                <div className="card-body">
                  <div className="h-64">
                    <ResponsiveContainer width="100%" height="100%">
                      <AreaChart data={reportData.daily_breakdown}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="date" />
                        <YAxis />
                        <Tooltip />
                        <Area type="monotone" dataKey="violations_count" stroke="#3b82f6" fill="#3b82f6" fillOpacity={0.3} />
                      </AreaChart>
                    </ResponsiveContainer>
                  </div>
                </div>
              </div>

              {/* Status Breakdown */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <div className="card">
                  <div className="card-header">
                    <h4 className="text-lg font-medium">Violations by Status</h4>
                  </div>
                  <div className="card-body">
                    <div className="h-64">
                      <ResponsiveContainer width="100%" height="100%">
                        <RechartsPieChart>
                          <Pie
                            data={reportData.violations_by_status}
                            cx="50%"
                            cy="50%"
                            labelLine={false}
                            label={({ status, count }) => `${status}: ${count}`}
                            outerRadius={80}
                            fill="#8884d8"
                            dataKey="count"
                          >
                            {reportData.violations_by_status.map((entry, index) => (
                              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                            ))}
                          </Pie>
                          <Tooltip />
                        </RechartsPieChart>
                      </ResponsiveContainer>
                    </div>
                  </div>
                </div>

                <div className="card">
                  <div className="card-header">
                    <h4 className="text-lg font-medium">Monthly Summary</h4>
                  </div>
                  <div className="card-body">
                    <div className="space-y-4">
                      <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                        <span className="text-sm font-medium text-gray-600">Average Daily Violations</span>
                        <span className="text-lg font-bold text-gray-900">{reportData.summary.avg_daily_violations}</span>
                      </div>
                      <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                        <span className="text-sm font-medium text-gray-600">Days with Violations</span>
                        <span className="text-lg font-bold text-gray-900">{reportData.daily_breakdown.filter(d => d.violations_count > 0).length}</span>
                      </div>
                      <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                        <span className="text-sm font-medium text-gray-600">Peak Day</span>
                        <span className="text-lg font-bold text-gray-900">
                          {reportData.daily_breakdown.reduce((max, day) => 
                            day.violations_count > max.violations_count ? day : max
                          , { violations_count: 0 }).date}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const Reports = () => {
  const [activeReport, setActiveReport] = useState(null);
  const [filters, setFilters] = useState({
    startDate: '',
    endDate: '',
    date: new Date().toISOString().split('T')[0],
    year: new Date().getFullYear(),
    month: new Date().getMonth() + 1
  });

  const reportTypes = [
    {
      id: 'violations',
      title: 'Violations Report',
      description: 'Comprehensive analysis of traffic violations with trends and statistics',
      icon: FileText,
      color: 'primary'
    },
    {
      id: 'enforcers',
      title: 'Enforcers Performance',
      description: 'Track enforcer productivity and performance metrics',
      icon: Users,
      color: 'success'
    },
    {
      id: 'daily-summary',
      title: 'Daily Summary',
      description: 'Quick overview of daily activities and key metrics',
      icon: Calendar,
      color: 'warning'
    },
    {
      id: 'monthly',
      title: 'Monthly Analysis',
      description: 'Monthly trends and comparative analysis',
      icon: TrendingUp,
      color: 'info'
    }
  ];

  const handleReportSelect = (reportType) => {
    setActiveReport(reportType);
    // Set default date range to last 30 days for date-based reports
    if (reportType === 'violations' || reportType === 'enforcers') {
      const endDate = new Date().toISOString().split('T')[0];
      const startDate = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];
      setFilters(prev => ({ ...prev, startDate, endDate }));
    }
  };

  const renderActiveReport = () => {
    switch (activeReport) {
      case 'violations':
        return <ViolationsReport filters={filters} setFilters={setFilters} />;
      case 'enforcers':
        return <EnforcersReport filters={filters} setFilters={setFilters} />;
      case 'daily-summary':
        return <DailySummaryReport filters={filters} setFilters={setFilters} />;
      case 'monthly':
        return <MonthlyReport filters={filters} setFilters={setFilters} />;
      default:
        return null;
    }
  };

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="bg-gradient-to-r from-primary-50 to-blue-50 rounded-2xl p-8 border border-primary-100">
        <div className="max-w-4xl">
          <h1 className="text-4xl font-bold text-gray-900 mb-3">Reports & Analytics</h1>
          <p className="text-lg text-gray-600 leading-relaxed">
            Generate comprehensive reports and analyze system performance with detailed insights and visualizations
          </p>
          <div className="mt-4 flex items-center gap-2 text-sm text-primary-600">
            <div className="w-2 h-2 bg-primary-500 rounded-full animate-pulse"></div>
            <span>Real-time data updates</span>
          </div>
        </div>
      </div>

      {!activeReport ? (
        /* Report Selection Grid */
        <div className="space-y-6">
          <div className="text-center mb-8">
            <h2 className="text-2xl font-semibold text-gray-800 mb-2">Choose Your Report Type</h2>
            <p className="text-gray-500">Select from our comprehensive suite of analytical reports</p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {reportTypes.map((report) => (
              <ReportCard
                key={report.id}
                title={report.title}
                description={report.description}
                icon={report.icon}
                color={report.color}
                onClick={() => handleReportSelect(report.id)}
              />
            ))}
          </div>
        </div>
      ) : (
        /* Active Report View */
        <div className="space-y-6">
          <div className="flex items-center justify-between bg-white rounded-xl p-6 shadow-sm border border-gray-100">
            <div className="flex items-center gap-4">
              <button
                onClick={() => setActiveReport(null)}
                className="btn-secondary flex items-center gap-2 hover:bg-gray-50 transition-colors"
              >
                <ArrowLeft className="h-4 w-4" />
                Back to Reports
              </button>
              <div className="h-6 w-px bg-gray-200"></div>
              <div>
                <h2 className="text-xl font-semibold text-gray-900">
                  {reportTypes.find(r => r.id === activeReport)?.title}
                </h2>
                <p className="text-sm text-gray-500">
                  {reportTypes.find(r => r.id === activeReport)?.description}
                </p>
              </div>
            </div>
            <div className="flex items-center gap-2 text-sm text-gray-500">
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
              <span>Live Data</span>
            </div>
          </div>
          {renderActiveReport()}
        </div>
      )}
    </div>
  );
};

export default Reports;
