import React from 'react';

const Logo = ({ size = 'default', className = '' }) => {
  const sizeClasses = {
    small: 'h-8 w-8',
    default: 'h-10 w-10',
    large: 'h-12 w-12'
  };

  const textSizes = {
    small: 'text-lg',
    default: 'text-2xl',
    large: 'text-3xl'
  };

  return (
    <div className={`flex items-center ${className}`}>
      {/* Logo Icon */}
      <div className={`${sizeClasses[size]} flex-shrink-0 rounded-lg bg-white ring-1 ring-gray-200 flex items-center justify-center shadow-lg overflow-hidden`}>
        <img
          src="/assets/traffic-lights.png"
          alt="Traffic lights logo"
          className="w-5/6 h-5/6 object-contain"
          loading="lazy"
          onError={(e) => { e.currentTarget.onerror = null; e.currentTarget.src = 'https://cdn-icons-png.flaticon.com/512/13481/13481653.png'; }}
        />
      </div>
      
      {/* Logo Text */}
      <div className="ml-3">
        <h1 className={`font-semibold tracking-tight ${textSizes[size]}`}>
          <span className="text-gray-900">e</span>
          <span className="text-primary-700">-Traffic</span>
        </h1>
        <p className="text-xs font-medium text-gray-500">System</p>
      </div>
    </div>
  );
};

export default Logo;
