import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Plus, Edit, Trash2, Eye, Search, Filter } from 'lucide-react';
import { adminAPI } from '../../services/api';
import toast from 'react-hot-toast';

const Enforcers = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingEnforcer, setEditingEnforcer] = useState(null);
  const [formErrors, setFormErrors] = useState({});
  const queryClient = useQueryClient();

  // Fetch enforcers
  const { data: enforcersResponse, isLoading } = useQuery({
    queryKey: ['enforcers'],
    queryFn: () => adminAPI.getEnforcers()
  });

  // Extract enforcers array from response
  const enforcers = enforcersResponse?.data?.enforcers || [];



  // Add enforcer mutation
  const addEnforcerMutation = useMutation({
    mutationFn: (enforcerData) => adminAPI.createEnforcer(enforcerData),
    onSuccess: (response) => {
      // Add the new enforcer to the cache immediately
      queryClient.setQueryData(['enforcers'], (oldData) => {
        if (oldData?.data?.enforcers) {
          const newData = {
            ...oldData,
            data: {
              ...oldData.data,
              enforcers: [response.data, ...oldData.data.enforcers]
            }
          };
          return newData;
        }
        return oldData;
      });

      // Also invalidate to ensure fresh data
      queryClient.invalidateQueries(['enforcers']);
      setShowAddModal(false);
      setFormErrors({});
      toast.success('Enforcer added successfully');
    },
    onError: (error) => {
      // Handle validation errors
      if (error.response?.data?.details) {
        const errors = {};
        error.response.data.details.forEach(detail => {
          errors[detail.path] = detail.msg;
        });
        setFormErrors(errors);
      } else {
        setFormErrors({});
        toast.error(error.response?.data?.message || 'Failed to add enforcer');
      }
    }
  });

  // Update enforcer mutation
  const updateEnforcerMutation = useMutation({
    mutationFn: ({ id, data }) => adminAPI.updateEnforcer(id, data),
    onSuccess: (response, variables) => {
      // Update the enforcer in the cache immediately
      queryClient.setQueryData(['enforcers'], (oldData) => {
        if (oldData?.data?.enforcers) {
          return {
            ...oldData,
            data: {
              ...oldData.data,
              enforcers: oldData.data.enforcers.map(enforcer => 
                enforcer.id === variables.id ? { ...enforcer, ...response.data } : enforcer
              )
            }
          };
        }
        return oldData;
      });

      // Also invalidate to ensure fresh data
      queryClient.invalidateQueries(['enforcers']);
      setEditingEnforcer(null);
      setFormErrors({});
      toast.success('Enforcer updated successfully');
    },
    onError: (error) => {
      // Handle validation errors
      if (error.response?.data?.details) {
        const errors = {};
        error.response.data.details.forEach(detail => {
          errors[detail.path] = detail.msg;
        });
        setFormErrors(errors);
      } else {
        setFormErrors({});
        toast.error(error.response?.data?.message || 'Failed to update enforcer');
      }
    }
  });

  // Delete enforcer mutation
  const deleteEnforcerMutation = useMutation({
    mutationFn: (id) => adminAPI.deleteEnforcer(id),
    onSuccess: () => {
      queryClient.invalidateQueries(['enforcers']);
      toast.success('Enforcer deleted successfully');
    },
    onError: (error) => {
      toast.error(error.response?.data?.message || 'Failed to delete enforcer');
    }
  });

  // Filter enforcers
  const filteredEnforcers = enforcers.filter(enforcer => {
    const matchesSearch = enforcer.full_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         enforcer.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         enforcer.badge_number?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus = statusFilter === 'all' || 
                         (statusFilter === 'active' && enforcer.is_active) ||
                         (statusFilter === 'inactive' && !enforcer.is_active);

    return matchesSearch && matchesStatus;
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const enforcerData = {
      username: formData.get('username'),
      email: formData.get('email'),
      password: formData.get('password'),
      full_name: formData.get('full_name'),
      badge_number: formData.get('badge_number'),
      phone_number: formData.get('phone_number'),
      is_active: formData.get('is_active') === 'true'
    };

    if (editingEnforcer) {
      updateEnforcerMutation.mutate({ id: editingEnforcer.id, data: enforcerData });
    } else {
      addEnforcerMutation.mutate(enforcerData);
    }
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this enforcer?')) {
      deleteEnforcerMutation.mutate(id);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Enforcer Management</h1>
          <p className="text-gray-600">Manage traffic enforcer accounts</p>
        </div>
        <button
          onClick={() => setShowAddModal(true)}
          className="btn-primary flex items-center gap-2"
        >
          <Plus className="w-4 h-4" />
          Add Enforcer
        </button>
      </div>

      {/* Filters */}
      <div className="bg-white rounded-lg shadow p-4">
        <div className="flex gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <input
                type="text"
                placeholder="Search enforcers..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 pr-4 py-2 w-full border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent"
              />
            </div>
          </div>
          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent"
          >
            <option value="all">All Status</option>
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
          </select>
        </div>
      </div>

      {/* Enforcers Table */}
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Enforcer
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Badge Number
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Contact
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Last Login
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredEnforcers.map((enforcer) => (
                <tr key={enforcer.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div>
                      <div className="text-sm font-medium text-gray-900">
                        {enforcer.full_name}
                      </div>
                      <div className="text-sm text-gray-500">
                        {enforcer.email}
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {enforcer.badge_number || 'N/A'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {enforcer.phone_number || 'N/A'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                      enforcer.is_active 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-red-100 text-red-800'
                    }`}>
                      {enforcer.is_active ? 'Active' : 'Inactive'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {enforcer.last_login 
                      ? new Date(enforcer.last_login).toLocaleDateString()
                      : 'Never'
                    }
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <div className="flex justify-end gap-2">
                      <button
                        onClick={() => setEditingEnforcer(enforcer)}
                        className="text-indigo-600 hover:text-indigo-900"
                      >
                        <Edit className="w-4 h-4" />
                      </button>
                      <button
                        onClick={() => handleDelete(enforcer.id)}
                        className="text-red-600 hover:text-red-900"
                      >
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {filteredEnforcers.length === 0 && (
          <div className="text-center py-8">
            <p className="text-gray-500">No enforcers found</p>
          </div>
        )}
      </div>

      {/* Add/Edit Modal */}
      {(showAddModal || editingEnforcer) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-xl font-bold mb-4">
              {editingEnforcer ? 'Edit Enforcer' : 'Add New Enforcer'}
            </h2>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Username
                </label>
                <input
                  type="text"
                  name="username"
                  defaultValue={editingEnforcer?.username || ''}
                  required
                  className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                    formErrors.username ? 'border-red-300' : 'border-gray-300'
                  }`}
                />
                {formErrors.username && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.username}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Email
                </label>
                <input
                  type="email"
                  name="email"
                  defaultValue={editingEnforcer?.email || ''}
                  required
                  className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                    formErrors.email ? 'border-red-300' : 'border-gray-300'
                  }`}
                />
                {formErrors.email && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.email}</p>
                )}
              </div>

              {!editingEnforcer && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Password
                  </label>
                  <input
                    type="password"
                    name="password"
                    required
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                      formErrors.password ? 'border-red-300' : 'border-gray-300'
                    }`}
                  />
                  {formErrors.password && (
                    <p className="mt-1 text-sm text-red-600">{formErrors.password}</p>
                  )}
                </div>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Full Name
                </label>
                <input
                  type="text"
                  name="full_name"
                  defaultValue={editingEnforcer?.full_name || ''}
                  required
                  className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                    formErrors.full_name ? 'border-red-300' : 'border-gray-300'
                  }`}
                />
                {formErrors.full_name && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.full_name}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Badge Number
                </label>
                <input
                  type="text"
                  name="badge_number"
                  defaultValue={editingEnforcer?.badge_number || ''}
                  required
                  className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                    formErrors.badge_number ? 'border-red-300' : 'border-gray-300'
                  }`}
                />
                {formErrors.badge_number && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.badge_number}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Phone Number
                </label>
                <input
                  type="tel"
                  name="phone_number"
                  defaultValue={editingEnforcer?.phone_number || ''}
                  pattern="[0-9+\-\s\(\)]+"
                  placeholder="Enter phone number"
                  className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                    formErrors.phone_number ? 'border-red-300' : 'border-gray-300'
                  }`}
                />
                {formErrors.phone_number && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.phone_number}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Status
                </label>
                <select
                  name="is_active"
                  defaultValue={editingEnforcer?.is_active?.toString() || 'true'}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent"
                >
                  <option value="true">Active</option>
                  <option value="false">Inactive</option>
                </select>
              </div>

              <div className="flex gap-3 pt-4">
                <button
                  type="submit"
                  disabled={addEnforcerMutation.isPending || updateEnforcerMutation.isPending}
                  className="flex-1 btn-primary"
                >
                  {addEnforcerMutation.isPending || updateEnforcerMutation.isPending ? 'Saving...' : 'Save'}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowAddModal(false);
                    setEditingEnforcer(null);
                    setFormErrors({});
                  }}
                  className="flex-1 btn-secondary"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Enforcers;
