import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { violationsAPI } from '../../services/api';
import { Search, Filter, Download, Eye, Edit, Trash2 } from 'lucide-react';
import LoadingSpinner from '../../components/LoadingSpinner';

const Violations = () => {
  const [filters, setFilters] = useState({
    page: 1,
    limit: 10,
    search: '',
    status: '',
    enforcer_id: '',
    start_date: '',
    end_date: '',
    violation_type: '',
  });

  const { data: violationsResponse, isLoading, error } = useQuery({
    queryKey: ['violations', filters],
    queryFn: () => violationsAPI.getViolations(filters),
    placeholderData: (previousData) => previousData,
  });

  // Extract data from response
  const data = violationsResponse?.data?.data;

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: 1, // Reset to first page when filters change
    }));
  };

  const handlePageChange = (page) => {
    setFilters(prev => ({ ...prev, page }));
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <p className="text-danger-600">Failed to load violations</p>
      </div>
    );
  }

  const { violations, pagination } = data?.data || {};

  return (
    <div className="space-y-6">
      {/* Page header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Violations</h1>
          <p className="mt-1 text-sm text-gray-500">
            Manage and monitor traffic violations
          </p>
        </div>
        <button className="btn-primary">
          <Download className="h-4 w-4 mr-2" />
          Export
        </button>
      </div>

      {/* Filters */}
      <div className="card">
        <div className="card-body">
          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
            {/* Search */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Search
              </label>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <input
                  type="text"
                  className="input pl-10"
                  placeholder="Search violations..."
                  value={filters.search}
                  onChange={(e) => handleFilterChange('search', e.target.value)}
                />
              </div>
            </div>

            {/* Status filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Status
              </label>
              <select
                className="input"
                value={filters.status}
                onChange={(e) => handleFilterChange('status', e.target.value)}
              >
                <option value="">All Status</option>
                <option value="pending">Pending</option>
                <option value="issued">Issued</option>
                <option value="paid">Paid</option>
                <option value="disputed">Disputed</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>

            {/* Violation type filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Violation Type
              </label>
              <select
                className="input"
                value={filters.violation_type}
                onChange={(e) => handleFilterChange('violation_type', e.target.value)}
              >
                <option value="">All Types</option>
                <option value="Speeding">Speeding</option>
                <option value="Red Light">Red Light</option>
                <option value="Parking">Parking</option>
                <option value="Reckless Driving">Reckless Driving</option>
                <option value="DUI">DUI</option>
              </select>
            </div>

            {/* Date range */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Start Date
              </label>
              <input
                type="date"
                className="input"
                value={filters.start_date}
                onChange={(e) => handleFilterChange('start_date', e.target.value)}
              />
            </div>
          </div>

          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3 mt-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                End Date
              </label>
              <input
                type="date"
                className="input"
                value={filters.end_date}
                onChange={(e) => handleFilterChange('end_date', e.target.value)}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Items per page
              </label>
              <select
                className="input"
                value={filters.limit}
                onChange={(e) => handleFilterChange('limit', parseInt(e.target.value))}
              >
                <option value={10}>10</option>
                <option value={25}>25</option>
                <option value={50}>50</option>
                <option value={100}>100</option>
              </select>
            </div>

            <div className="flex items-end">
              <button
                onClick={() => setFilters({
                  page: 1,
                  limit: 10,
                  search: '',
                  status: '',
                  enforcer_id: '',
                  start_date: '',
                  end_date: '',
                  violation_type: '',
                })}
                className="btn-secondary"
              >
                <Filter className="h-4 w-4 mr-2" />
                Clear Filters
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Violations table */}
      <div className="card">
        <div className="card-body">
          <div className="overflow-x-auto">
            <table className="table">
              <thead className="table-header">
                <tr>
                  <th className="table-header-cell">Violation #</th>
                  <th className="table-header-cell">Violator</th>
                  <th className="table-header-cell">Vehicle</th>
                  <th className="table-header-cell">Type</th>
                  <th className="table-header-cell">Fine</th>
                  <th className="table-header-cell">Status</th>
                  <th className="table-header-cell">Enforcer</th>
                  <th className="table-header-cell">Date</th>
                  <th className="table-header-cell">Actions</th>
                </tr>
              </thead>
              <tbody className="table-body">
                {violations?.map((violation) => (
                  <tr key={violation.id} className="table-row">
                    <td className="table-cell font-medium">
                      {violation.violation_number}
                    </td>
                    <td className="table-cell">
                      <div>
                        <div className="font-medium">{violation.violator_name}</div>
                        <div className="text-sm text-gray-500">
                          {violation.violator_phone}
                        </div>
                      </div>
                    </td>
                    <td className="table-cell">
                      <div>
                        <div className="font-medium">{violation.vehicle_plate}</div>
                        <div className="text-sm text-gray-500">
                          {violation.vehicle_model} {violation.vehicle_color}
                        </div>
                      </div>
                    </td>
                    <td className="table-cell">{violation.violation_type}</td>
                    <td className="table-cell font-medium">
                      ${violation.fine_amount}
                    </td>
                    <td className="table-cell">
                      <StatusBadge status={violation.status} />
                    </td>
                    <td className="table-cell">
                      <div>
                        <div className="font-medium">{violation.enforcer_name}</div>
                        <div className="text-sm text-gray-500">
                          {violation.enforcer_badge}
                        </div>
                      </div>
                    </td>
                    <td className="table-cell">
                      {new Date(violation.created_at).toLocaleDateString()}
                    </td>
                    <td className="table-cell">
                      <div className="flex space-x-2">
                        <button className="text-primary-600 hover:text-primary-900">
                          <Eye className="h-4 w-4" />
                        </button>
                        <button className="text-warning-600 hover:text-warning-900">
                          <Edit className="h-4 w-4" />
                        </button>
                        <button className="text-danger-600 hover:text-danger-900">
                          <Trash2 className="h-4 w-4" />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {(!violations || violations.length === 0) && (
              <div className="text-center py-8 text-gray-500">
                No violations found
              </div>
            )}
          </div>

          {/* Pagination */}
          {pagination && pagination.total > 1 && (
            <div className="mt-6 flex items-center justify-between">
              <div className="text-sm text-gray-700">
                Showing {((pagination.current - 1) * filters.limit) + 1} to{' '}
                {Math.min(pagination.current * filters.limit, pagination.totalRecords)} of{' '}
                {pagination.totalRecords} results
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => handlePageChange(pagination.current - 1)}
                  disabled={pagination.current === 1}
                  className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Previous
                </button>
                <span className="px-3 py-2 text-sm text-gray-700">
                  Page {pagination.current} of {pagination.total}
                </span>
                <button
                  onClick={() => handlePageChange(pagination.current + 1)}
                  disabled={pagination.current === pagination.total}
                  className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Next
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const StatusBadge = ({ status }) => {
  const statusConfig = {
    pending: { color: 'warning', text: 'Pending' },
    issued: { color: 'info', text: 'Issued' },
    paid: { color: 'success', text: 'Paid' },
    disputed: { color: 'danger', text: 'Disputed' },
    cancelled: { color: 'secondary', text: 'Cancelled' },
  };

  const config = statusConfig[status] || statusConfig.pending;

  return (
    <span className={`badge-${config.color}`}>
      {config.text}
    </span>
  );
};

export default Violations;
