const DailySummaryReport = ({ filters, setFilters }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [reportData, setReportData] = useState(null);
  const [error, setError] = useState(null);
  const [lastRefresh, setLastRefresh] = useState(new Date());

  // Auto-load data when component mounts
  useEffect(() => {
    generateReport();
  }, []);

  // Auto-refresh data every 10 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      if (!isLoading) {
        generateReport();
      }
    }, 10000);

    return () => clearInterval(interval);
  }, [isLoading]);

  const generateReport = async () => {
    setIsLoading(true);
    setError(null);
    try {
      console.log('Generating daily summary report for date:', filters.date || new Date().toISOString().split('T')[0]);

      const response = await reportsAPI.getDailySummary({
        date: filters.date || new Date().toISOString().split('T')[0]
      });

      console.log('Daily summary response:', response);

      if (response.data.success) {
        setReportData(response.data.data);
        setLastRefresh(new Date());
        console.log('Daily summary data set:', response.data.data);

        // Show success message if data was loaded
        if (response.data.data && response.data.data.summary) {
          toast.success(`Daily summary loaded: ${response.data.data.summary.total_violations || 0} violations found`, { duration: 2000 });
        }
      } else {
        setError('Failed to generate report: ' + (response.data.error || 'Unknown error'));
      }
    } catch (error) {
      console.error('Failed to generate daily summary report:', error);
      console.error('Error response:', error.response);
      console.error('Error message:', error.message);

      if (error.response?.status === 500) {
        setError('Server error: Database connection or query failed');
      } else if (error.response?.status === 404) {
        setError('Report endpoint not found');
      } else if (error.response?.status === 401) {
        setError('Authentication failed. Please login again.');
      } else if (error.code === 'ERR_NETWORK') {
        setError('Network error: Cannot connect to server');
      } else {
        setError(error.response?.data?.error || error.message || 'Failed to generate report');
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-xl font-bold text-gray-900">Daily Summary Report</h2>
          <p className="text-gray-600">Quick overview of daily activities and key metrics</p>
          <div className="mt-2 flex items-center gap-2 text-sm text-gray-500">
            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
            <span>Live Data</span>
            <span>• Last updated: {lastRefresh.toLocaleTimeString()}</span>
            <span>• Status: {isLoading ? '⏳ Loading...' : reportData ? '✅ Active' : '❌ No Data'}</span>
          </div>
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <div className="flex">
            <AlertCircle className="h-5 w-5 text-red-400" />
            <div className="ml-3">
              <p className="text-sm text-red-800">{error}</p>
              <div className="mt-2 text-xs text-red-600">
                <p>• Check if the server is running</p>
                <p>• Verify database connection</p>
                <p>• Check API endpoint configuration</p>
                <p>• Verify the date selected has data</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Data Status Indicator */}
      <div className="bg-blue-50 border border-blue-200 rounded-md p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
            <span className="text-sm text-blue-800">Data Status</span>
          </div>
          <div className="text-xs text-blue-600">
            {reportData ? (
              <span>✅ Data loaded successfully</span>
            ) : isLoading ? (
              <span>⏳ Loading data...</span>
            ) : (
              <span>❌ No data available</span>
            )}
          </div>
        </div>
        {reportData && (
          <div className="mt-2 text-xs text-blue-600">
            <p>• Violations: {reportData.summary?.total_violations || 0}</p>
            <p>• Total Fines: ₱{(reportData.summary?.total_fines || 0).toFixed(2)}</p>
            <p>• Active Enforcers: {reportData.summary?.active_enforcers || 0}</p>
            <p>• Last updated: {lastRefresh.toLocaleTimeString()}</p>
          </div>
