import React, { Suspense, useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Float, Environment, Lightformer } from '@react-three/drei';

function SpinningTorus() {
  const meshRef = useRef();
  useFrame((state, delta) => {
    if (!meshRef.current) return;
    meshRef.current.rotation.x += delta * 0.1;
    meshRef.current.rotation.y += delta * 0.15;
  });
  return (
    <Float speed={1} rotationIntensity={0.4} floatIntensity={0.6}>
      <mesh ref={meshRef} castShadow receiveShadow>
        <torusKnotGeometry args={[1.2, 0.35, 180, 32]} />
        <meshPhysicalMaterial
          color="#2563eb"
          metalness={0.6}
          roughness={0.2}
          clearcoat={0.6}
          transmission={0}
          reflectivity={0.9}
        />
      </mesh>
    </Float>
  );
}

const Lights = () => (
  <group>
    <ambientLight intensity={0.4} />
    <directionalLight position={[5, 5, 5]} intensity={0.8} castShadow />
    <directionalLight position={[-5, -5, -5]} intensity={0.3} />
    <Environment resolution={256} background={false}>
      <Lightformer form="ring" intensity={2} rotation-x={Math.PI / 2} position={[0, 5, -10]} scale={[10, 10, 1]} />
    </Environment>
  </group>
);

const ThreeBackground = () => {
  return (
    <div aria-hidden className="pointer-events-none fixed inset-0 -z-10">
      <Canvas shadows camera={{ position: [0, 0, 6], fov: 50 }}>
        <color attach="background" args={[0.96, 0.98, 1]} />
        <Suspense fallback={null}>
          <Lights />
          <SpinningTorus />
          {/* Soft ground shadow plane */}
          <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, -2, 0]} receiveShadow>
            <planeGeometry args={[100, 100]} />
            <shadowMaterial transparent opacity={0.2} />
          </mesh>
        </Suspense>
        {/* Disable user controls to keep purely decorative */}
        <OrbitControls enabled={false} />
      </Canvas>
    </div>
  );
};

export default ThreeBackground;
