import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { violationsAPI, adminAPI } from '../../services/api';
import { Search, Filter, Download, Eye, Edit, Trash2 } from 'lucide-react';
import LoadingSpinner from '../../components/LoadingSpinner';
import toast from 'react-hot-toast';

const Violations = () => {
  const [filters, setFilters] = useState({
    page: 1,
    limit: 10,
    search: '',
    status: '',
    enforcer_id: '',
    start_date: '',
    end_date: '',
    violation_type: '',
  });
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingViolation, setEditingViolation] = useState(null);
  const [formErrors, setFormErrors] = useState({});
  const [isExporting, setIsExporting] = useState(false);
  const queryClient = useQueryClient();

  const { data: violationsResponse, isLoading, error } = useQuery({
    queryKey: ['violations', filters],
    queryFn: () => violationsAPI.getViolations(filters),
    placeholderData: (previousData) => previousData,
  });

  // Query for dashboard stats to get accurate counts
  const { data: dashboardStats } = useQuery({
    queryKey: ['violationStats'],
    queryFn: () => adminAPI.getDashboard(),
    staleTime: 30000, // 30 seconds
  });

  // Extract data from response
  const data = violationsResponse?.data?.data;
  const { violations, pagination } = data || {};

  // Update violation mutation
  const updateViolationMutation = useMutation({
    mutationFn: ({ id, data }) => violationsAPI.updateViolation(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries(['violations', filters]);
      queryClient.invalidateQueries(['adminDashboard']);
      queryClient.invalidateQueries(['violationStats']);
      setShowEditModal(false);
      setEditingViolation(null);
      setFormErrors({});
      toast.success('Violation updated successfully');
    },
    onError: (error) => {
      if (error.response?.data?.details) {
        const errors = {};
        error.response.data.details.forEach(detail => {
          errors[detail.param || detail.path] = detail.msg;
        });
        setFormErrors(errors);
      } else {
        setFormErrors({});
        toast.error(error.response?.data?.message || 'Failed to update violation');
      }
    }
  });

  // Delete violation mutation
  const deleteViolationMutation = useMutation({
    mutationFn: (id) => violationsAPI.deleteViolation(id),
    onSuccess: () => {
      queryClient.invalidateQueries(['violations', filters]);
      queryClient.invalidateQueries(['adminDashboard']);
      queryClient.invalidateQueries(['violationStats']);
      toast.success('Violation deleted successfully');
    },
    onError: (error) => {
      toast.error(error.response?.data?.message || 'Failed to delete violation');
    }
  });

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: 1, // Reset to first page when filters change
    }));
  };

  const handlePageChange = (page) => {
    setFilters(prev => ({ ...prev, page }));
  };

  const handleEdit = (violation) => {
    setEditingViolation(violation);
    setShowEditModal(true);
    setFormErrors({});
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this violation? This action cannot be undone.')) {
      deleteViolationMutation.mutate(id);
    }
  };

  const handleExport = async () => {
    try {
      setIsExporting(true);
      toast.loading('Preparing export...', { id: 'export' });
      
      // Prepare export parameters with current filters (exclude pagination)
      const exportFilters = {
        search: filters.search || '',
        status: filters.status || '',
        enforcer_id: filters.enforcer_id || '',
        start_date: filters.start_date || '',
        end_date: filters.end_date || '',
        violation_type: filters.violation_type || '',
        format: 'csv'
      };

      console.log('Export filters:', exportFilters);
      const response = await violationsAPI.exportViolations(exportFilters);
      console.log('Export response status:', response.status);
      
      if (!response.ok) {
        const errorText = await response.text();
        console.error('Export error response:', errorText);
        throw new Error(`Export failed: ${response.status} - ${errorText}`);
      }
      
      // Get the CSV content as blob
      const blob = await response.blob();
      console.log('Export blob size:', blob.size);
      
      // Create download link
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      
      // Generate filename with current date and filters
      const today = new Date().toISOString().split('T')[0];
      let filename = `violations_export_${today}`;
      if (filters.status) filename += `_${filters.status}`;
      if (filters.start_date && filters.end_date) {
        filename += `_${filters.start_date}_to_${filters.end_date}`;
      }
      filename += '.csv';
      
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
      
      toast.success('Export completed successfully!', { id: 'export' });
    } catch (error) {
      console.error('Export error:', error);
      console.error('Error details:', {
        message: error.message,
        stack: error.stack
      });
      
      let errorMessage = 'Failed to export violations';
      if (error.message.includes('401')) {
        errorMessage = 'Authentication failed. Please login again.';
      } else if (error.message.includes('403')) {
        errorMessage = 'You do not have permission to export violations.';
      } else if (error.message.includes('500')) {
        errorMessage = 'Server error occurred during export.';
      } else if (error.message) {
        errorMessage = error.message;
      }
      
      toast.error(errorMessage, { id: 'export' });
    } finally {
      setIsExporting(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const violationData = {
      status: formData.get('status'),
      notes: formData.get('notes')
    };

    updateViolationMutation.mutate({ 
      id: editingViolation.id, 
      data: violationData 
    });
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <p className="text-danger-600">Failed to load violations</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Page header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Traffic Violations</h1>
          <p className="mt-1 text-sm text-gray-500">
            View and manage violations recorded by enforcers via IoT devices
          </p>
        </div>
        <div className="flex gap-2">
          <button 
            onClick={() => handleFilterChange('status', filters.status === 'pending' ? '' : 'pending')}
            className={`btn-${filters.status === 'pending' ? 'primary' : 'warning'}`}
            title={filters.status === 'pending' ? 'Show all violations' : 'Show only pending violations'}
          >
            <Eye className="h-4 w-4 mr-2" />
            {filters.status === 'pending' ? 'Show All' : 'View Pending'}
          </button>
          <button 
            onClick={() => {
              queryClient.invalidateQueries(['violations', filters]);
              queryClient.invalidateQueries(['violationStats']);
            }}
            className="btn-secondary"
          >
            <Search className="h-4 w-4 mr-2" />
            Refresh
          </button>
          <button 
            onClick={handleExport}
            disabled={isExporting}
            className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
            title="Export current violations to CSV"
          >
            {isExporting ? (
              <LoadingSpinner size="sm" className="mr-2" />
            ) : (
              <Download className="h-4 w-4 mr-2" />
            )}
            {isExporting ? 'Exporting...' : 'Export'}
          </button>
        </div>
      </div>

      {/* Quick Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="bg-white rounded-lg border border-gray-200 p-4">
          <div className="flex items-center">
            <div className="flex-1">
              <p className="text-sm font-medium text-gray-600">Total Violations</p>
              <p className="text-2xl font-bold text-gray-900">{dashboardStats?.data?.data?.totalViolations || 0}</p>
            </div>
            <div className="p-3 bg-primary-100 rounded-full">
              <svg className="h-6 w-6 text-primary-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
            </div>
          </div>
        </div>
        
        <div className="bg-white rounded-lg border border-gray-200 p-4">
          <div className="flex items-center">
            <div className="flex-1">
              <p className="text-sm font-medium text-gray-600">Pending Violations</p>
              <p className="text-2xl font-bold text-warning-600">
                {dashboardStats?.data?.data?.violationsByStatus?.find(s => s.status === 'pending')?.count || 0}
              </p>
            </div>
            <div className="p-3 bg-warning-100 rounded-full">
              <svg className="h-6 w-6 text-warning-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
          </div>
        </div>
        
        <div className="bg-white rounded-lg border border-gray-200 p-4">
          <div className="flex items-center">
            <div className="flex-1">
              <p className="text-sm font-medium text-gray-600">Paid Violations</p>
              <p className="text-2xl font-bold text-success-600">
                {dashboardStats?.data?.data?.violationsByStatus?.find(s => s.status === 'paid')?.count || 0}
              </p>
            </div>
            <div className="p-3 bg-success-100 rounded-full">
              <svg className="h-6 w-6 text-success-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
          </div>
        </div>
        
        <div className="bg-white rounded-lg border border-gray-200 p-4">
          <div className="flex items-center">
            <div className="flex-1">
              <p className="text-sm font-medium text-gray-600">Disputed</p>
              <p className="text-2xl font-bold text-danger-600">
                {dashboardStats?.data?.data?.violationsByStatus?.find(s => s.status === 'disputed')?.count || 0}
              </p>
            </div>
            <div className="p-3 bg-danger-100 rounded-full">
              <svg className="h-6 w-6 text-danger-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
          </div>
        </div>
      </div>

      {/* Info Banner */}
      <div className={`border rounded-lg p-4 ${
        filters.status === 'pending' 
          ? 'bg-warning-50 border-warning-200' 
          : 'bg-blue-50 border-blue-200'
      }`}>
        <div className="flex">
          <div className="flex-shrink-0">
            {filters.status === 'pending' ? (
              <svg className="h-5 w-5 text-warning-400" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd" />
              </svg>
            ) : (
              <svg className="h-5 w-5 text-blue-400" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
              </svg>
            )}
          </div>
          <div className="ml-3">
            <h3 className={`text-sm font-medium ${
              filters.status === 'pending' ? 'text-warning-800' : 'text-blue-800'
            }`}>
              {filters.status === 'pending' ? 'Pending Violations View' : 'IoT Device Integration'}
            </h3>
            <div className={`mt-2 text-sm ${
              filters.status === 'pending' ? 'text-warning-700' : 'text-blue-700'
            }`}>
              <p>
                {filters.status === 'pending' 
                  ? 'You are currently viewing pending violations that require review and processing. These violations were recorded by enforcers and are awaiting status updates.' 
                  : 'Violations are automatically recorded by enforcers using handheld IoT devices. This interface allows administrators to view, manage, and process the recorded violations.'
                }
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Filters */}
      <div className="card">
        <div className="card-body">
          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
            {/* Search */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Search
              </label>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <input
                  type="text"
                  className="input pl-10"
                  placeholder="Search violations..."
                  value={filters.search}
                  onChange={(e) => handleFilterChange('search', e.target.value)}
                />
              </div>
            </div>

            {/* Status filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Status
              </label>
              <select
                className="input"
                value={filters.status}
                onChange={(e) => handleFilterChange('status', e.target.value)}
              >
                <option value="">All Status</option>
                <option value="pending">Pending</option>
                <option value="issued">Issued</option>
                <option value="paid">Paid</option>
                <option value="disputed">Disputed</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>

            {/* Violation type filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Violation Type
              </label>
              <select
                className="input"
                value={filters.violation_type}
                onChange={(e) => handleFilterChange('violation_type', e.target.value)}
              >
                <option value="">All Types</option>
                <option value="Speeding">Speeding</option>
                <option value="Red Light">Red Light</option>
                <option value="Parking">Parking</option>
                <option value="Reckless Driving">Reckless Driving</option>
                <option value="DUI">DUI</option>
              </select>
            </div>

            {/* Date range */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Start Date
              </label>
              <input
                type="date"
                className="input"
                value={filters.start_date}
                onChange={(e) => handleFilterChange('start_date', e.target.value)}
              />
            </div>
          </div>

          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3 mt-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                End Date
              </label>
              <input
                type="date"
                className="input"
                value={filters.end_date}
                onChange={(e) => handleFilterChange('end_date', e.target.value)}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Items per page
              </label>
              <select
                className="input"
                value={filters.limit}
                onChange={(e) => handleFilterChange('limit', parseInt(e.target.value))}
              >
                <option value={10}>10</option>
                <option value={25}>25</option>
                <option value={50}>50</option>
                <option value={100}>100</option>
              </select>
            </div>

            <div className="flex items-end">
              <button
                onClick={() => setFilters({
                  page: 1,
                  limit: 10,
                  search: '',
                  status: '',
                  enforcer_id: '',
                  start_date: '',
                  end_date: '',
                  violation_type: '',
                })}
                className="btn-secondary"
              >
                <Filter className="h-4 w-4 mr-2" />
                Clear Filters
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Violations table */}
      <div className="card">
        <div className="card-body">
          <div className="overflow-x-auto">
            <table className="table">
              <thead className="table-header">
                <tr>
                  <th className="table-header-cell">Violation #</th>
                  <th className="table-header-cell">Violator</th>
                  <th className="table-header-cell">Vehicle</th>
                  <th className="table-header-cell">Type</th>
                  <th className="table-header-cell">Fine</th>
                  <th className="table-header-cell">Status</th>
                  <th className="table-header-cell">Enforcer</th>
                  <th className="table-header-cell">Date</th>
                  <th className="table-header-cell">Actions</th>
                </tr>
              </thead>
              <tbody className="table-body">
                {violations?.map((violation) => (
                  <tr key={violation.id} className="table-row">
                    <td className="table-cell font-medium">
                      {violation.violation_number}
                    </td>
                    <td className="table-cell">
                      <div>
                        <div className="font-medium">{violation.violator_name}</div>
                        <div className="text-sm text-gray-500">
                          {violation.violator_phone}
                        </div>
                      </div>
                    </td>
                    <td className="table-cell">
                      <div>
                        <div className="font-medium">{violation.vehicle_plate}</div>
                        <div className="text-sm text-gray-500">
                          {violation.vehicle_model} {violation.vehicle_color}
                        </div>
                      </div>
                    </td>
                    <td className="table-cell">{violation.violation_type}</td>
                    <td className="table-cell font-medium">
                      ₱{violation.fine_amount?.toLocaleString()}
                    </td>
                    <td className="table-cell">
                      <StatusBadge status={violation.status} />
                    </td>
                    <td className="table-cell">
                      <div>
                        <div className="font-medium">{violation.enforcer_name}</div>
                        <div className="text-sm text-gray-500">
                          {violation.enforcer_badge}
                        </div>
                      </div>
                    </td>
                    <td className="table-cell">
                      {new Date(violation.created_at).toLocaleDateString()}
                    </td>
                    <td className="table-cell">
                      <div className="flex space-x-2">
                        <button className="text-primary-600 hover:text-primary-900">
                          <Eye className="h-4 w-4" />
                        </button>
                        <button 
                          onClick={() => handleEdit(violation)}
                          className="text-warning-600 hover:text-warning-900"
                        >
                          <Edit className="h-4 w-4" />
                        </button>
                        <button 
                          onClick={() => handleDelete(violation.id)}
                          className="text-danger-600 hover:text-danger-900"
                        >
                          <Trash2 className="h-4 w-4" />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {(!violations || violations.length === 0) && (
              <div className="text-center py-8 text-gray-500">
                <div className="mb-4">
                  <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                  </svg>
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">No violations found</h3>
                <p className="text-gray-500">Violations will appear here once enforcers record them using their IoT devices.</p>
              </div>
            )}
          </div>

          {/* Pagination */}
          {pagination && pagination.total > 1 && (
            <div className="mt-6 flex items-center justify-between">
              <div className="text-sm text-gray-700">
                Showing {((pagination.current - 1) * filters.limit) + 1} to{' '}
                {Math.min(pagination.current * filters.limit, pagination.totalRecords)} of{' '}
                {pagination.totalRecords} results
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => handlePageChange(pagination.current - 1)}
                  disabled={pagination.current === 1}
                  className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Previous
                </button>
                <span className="px-3 py-2 text-sm text-gray-700">
                  Page {pagination.current} of {pagination.total}
                </span>
                <button
                  onClick={() => handlePageChange(pagination.current + 1)}
                  disabled={pagination.current === pagination.total}
                  className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Next
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Edit Violation Modal */}
      {showEditModal && editingViolation && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-xl font-bold mb-4">Edit Violation</h2>
            
            <form onSubmit={handleSubmit} className="space-y-4">
              {/* Violation Details (Read-only) */}
              <div className="bg-gray-50 p-3 rounded-lg">
                <h3 className="font-medium text-gray-900 mb-2">Violation Details</h3>
                <div className="text-sm text-gray-600 space-y-1">
                  <p><span className="font-medium">Number:</span> {editingViolation.violation_number}</p>
                  <p><span className="font-medium">Type:</span> {editingViolation.violation_type}</p>
                  <p><span className="font-medium">Violator:</span> {editingViolation.violator_name}</p>
                  <p><span className="font-medium">Fine:</span> ₱{editingViolation.fine_amount?.toLocaleString()}</p>
                  <p><span className="font-medium">Location:</span> {editingViolation.location}</p>
                </div>
              </div>

              {/* Status */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Status *
                </label>
                <select
                  name="status"
                  defaultValue={editingViolation.status}
                  required
                  className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                    formErrors.status ? 'border-red-300' : 'border-gray-300'
                  }`}
                >
                  <option value="pending">Pending</option>
                  <option value="issued">Issued</option>
                  <option value="paid">Paid</option>
                  <option value="disputed">Disputed</option>
                  <option value="cancelled">Cancelled</option>
                </select>
                {formErrors.status && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.status}</p>
                )}
              </div>

              {/* Notes */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Administrative Notes
                </label>
                <textarea
                  name="notes"
                  rows="3"
                  defaultValue={editingViolation.notes || ''}
                  placeholder="Add administrative notes or updates..."
                  className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent ${
                    formErrors.notes ? 'border-red-300' : 'border-gray-300'
                  }`}
                />
                {formErrors.notes && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.notes}</p>
                )}
              </div>

              <div className="flex gap-3 pt-4">
                <button
                  type="submit"
                  disabled={updateViolationMutation.isPending}
                  className="flex-1 btn-primary"
                >
                  {updateViolationMutation.isPending ? 'Updating...' : 'Update Violation'}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowEditModal(false);
                    setEditingViolation(null);
                    setFormErrors({});
                  }}
                  className="flex-1 btn-secondary"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

const StatusBadge = ({ status }) => {
  const statusConfig = {
    pending: { color: 'warning', text: 'Pending' },
    issued: { color: 'info', text: 'Issued' },
    paid: { color: 'success', text: 'Paid' },
    disputed: { color: 'danger', text: 'Disputed' },
    cancelled: { color: 'secondary', text: 'Cancelled' },
  };

  const config = statusConfig[status] || statusConfig.pending;

  return (
    <span className={`badge-${config.color}`}>
      {config.text}
    </span>
  );
};

export default Violations;
